#!/usr/bin/env node
'use strict';

const path = require('path');

// Commands
const { scaffoldCommand } = require(path.join(__dirname, '..', 'lib', 'commands', 'scaffold'));
const { initCommand, initInteractiveCommand } = require(path.join(__dirname, '..', 'lib', 'commands', 'init'));
const { learnCommand } = require(path.join(__dirname, '..', 'lib', 'commands', 'learn'));
const { setupCommand } = require(path.join(__dirname, '..', 'lib', 'commands', 'setup'));
const { analyzeCommand } = require(path.join(__dirname, '..', 'lib', 'commands', 'analyze'));
const { planCommand } = require(path.join(__dirname, '..', 'lib', 'commands', 'plan'));
const { createIssuesCommand } = require(path.join(__dirname, '..', 'lib', 'commands', 'create-issues'));

// Utilities
const { parseArgs } = require(path.join(__dirname, '..', 'lib', 'utils', 'args-parser'));
const { usage } = require(path.join(__dirname, '..', 'lib', 'utils', 'cli-help'));
const { checkRequirements } = require(path.join(__dirname, '..', 'lib', 'utils', 'requirements'));

function main() {
  const args = parseArgs(process.argv);
  const cmd = args._[0];
  const cwd = process.cwd();
  if (cmd === 'init') {
    if (!checkRequirements(process.cwd())) { process.exitCode = 1; return; }
    if (!args.primary && (process.stdin.isTTY || process.env.FORCE_CLI_INTERACTIVE)) {
      initInteractiveCommand(cwd, args).then((code)=>{ process.exitCode = code; });
      return;
    }
    process.exitCode = initCommand(cwd, args);
    return;
  }
  if (cmd === 'learn') {
    if (!checkRequirements(process.cwd())) { process.exitCode = 1; return; }
    Promise.resolve(learnCommand(cwd, args)).then((code)=>{ process.exitCode = code; });
    return;
  }
  if (cmd === 'setup') {
    if (!checkRequirements(process.cwd())) { process.exitCode = 1; return; }
    Promise.resolve(setupCommand(cwd, args)).then((code)=>{ process.exitCode = code; });
    return;
  }
  if (cmd === 'analyze') {
    Promise.resolve(analyzeCommand(cwd, args)).then((code)=>{ process.exitCode = code; });
    return;
  }
  if (cmd === 'plan') {
    Promise.resolve(planCommand(cwd, args)).then((code)=>{ process.exitCode = code; });
    return;
  }
  if (cmd === 'create-issues') {
    Promise.resolve(createIssuesCommand(cwd, args)).then((code)=>{ process.exitCode = code; });
    return;
  }
  if (cmd === 'scaffold' || cmd === 'create-constants') {
    const dom = args._[1];
    const outDir = args.dir || args.out;
    process.exitCode = scaffoldCommand(cwd, dom, outDir);
    return;
  }
  usage();
}

main();

/**
 * @fileoverview Detect redundant calculations that should be computed at compile time
 * @author Doug
 */

'use strict';

//------------------------------------------------------------------------------
// Rule Definition
//------------------------------------------------------------------------------

/**
 * Evaluates a binary expression with literal operands
 * @param {ASTNode} node - The BinaryExpression node
 * @returns {number|null} - The evaluated result or null if cannot evaluate
 */
function evaluateExpression(node) {
  // Only evaluate if both sides are literals
  if (node.left.type !== 'Literal' || node.right.type !== 'Literal') {
    return null;
  }

  const left = node.left.value;
  const right = node.right.value;

  // Only evaluate numeric literals
  if (typeof left !== 'number' || typeof right !== 'number') {
    return null;
  }

  switch (node.operator) {
    case '+':
      return left + right;
    case '-':
      return left - right;
    case '*':
      return left * right;
    case '/':
      return left / right;
    case '%':
      return left % right;
    case '**':
      return left ** right;
    default:
      return null;
  }
}

/**
 * Checks if an expression tree contains only literals
 * @param {ASTNode} node - The expression node
 * @returns {boolean} - True if all operands are literals
 */
function isAllLiterals(node) {
  if (node.type === 'Literal') {
    return true;
  }

  if (node.type === 'BinaryExpression') {
    return isAllLiterals(node.left) && isAllLiterals(node.right);
  }

  return false;
}

/**
 * Recursively evaluates a binary expression tree
 * @param {ASTNode} node - The expression node
 * @returns {number|null} - The evaluated result
 */
function evaluateTree(node) {
  if (node.type === 'Literal') {
    return node.value;
  }

  if (node.type === 'BinaryExpression') {
    const left = evaluateTree(node.left);
    const right = evaluateTree(node.right);

    if (left === null || right === null) {
      return null;
    }

    switch (node.operator) {
      case '+':
        return left + right;
      case '-':
        return left - right;
      case '*':
        return left * right;
      case '/':
        return left / right;
      case '%':
        return left % right;
      case '**':
        return left ** right;
      default:
        return null;
    }
  }

  return null;
}

module.exports = {
  meta: {
    type: 'suggestion',
    docs: {
      description: 'Detect redundant calculations that should be computed at compile time',
      category: 'Best Practices',
      recommended: true,
      url: 'https://github.com/yourusername/eslint-plugin-ai-code-snifftest/blob/main/docs/rules/no-redundant-calculations.md'
    },
    fixable: 'code',
    schema: [
      {
        type: 'object',
        properties: {
          allowedOperators: {
            type: 'array',
            items: {
              type: 'string',
              enum: ['+', '-', '*', '/', '%', '**']
            },
            uniqueItems: true
          }
        },
        additionalProperties: false
      }
    ],
    messages: {
      redundantCalculation: 'Calculate this at compile time: {{ result }}',
      complexCalculation: 'This calculation can be simplified to: {{ result }}'
    }
  },

  create(context) {
    const options = context.options[0] || {};
    const allowedOperators = options.allowedOperators || ['+', '-', '*', '/', '%', '**'];

    return {
      BinaryExpression(node) {
        // Skip if operator is not in allowed list
        if (!allowedOperators.includes(node.operator)) {
          return;
        }

        // Skip if not all operands are literals
        if (!isAllLiterals(node)) {
          return;
        }

        // Evaluate the expression
        const computedValue = evaluateTree(node);

        if (computedValue === null) {
          return;
        }

        // Report the issue
        context.report({
          node,
          messageId: node.type === 'BinaryExpression' && 
                     (node.left.type === 'BinaryExpression' || node.right.type === 'BinaryExpression')
            ? 'complexCalculation'
            : 'redundantCalculation',
          data: {
            result: String(computedValue)
          },
          fix(fixer) {
            // Replace the entire expression with the calculated value
            return fixer.replaceText(node, String(computedValue));
          }
        });
      }
    };
  }
};

'use strict';

function push(map, key, item) {
  if (!map[key]) map[key] = [];
  map[key].push(item);
}

function categorizeViolations(eslintJson /* array */, cfg) {
  const out = {
    magicNumbers: [],
    complexity: [],
    domainTerms: [],
    architecture: [],
    counts: { errors: 0, warnings: 0, autoFixable: 0 }
  };
  const files = Array.isArray(eslintJson) ? eslintJson : [];
  for (const f of files) {
    // Use file-level fixable counts when available for accurate totals
    const fileFixable = (f && ((f.fixableWarningCount || 0) + (f.fixableErrorCount || 0))) || 0;
    let usedFileFixable = false;
    if (fileFixable > 0) {
      out.counts.autoFixable += fileFixable;
      usedFileFixable = true;
    }
    const messages = Array.isArray(f && f.messages) ? f.messages : [];
    for (const m of messages) {
      const rule = String(m.ruleId || '').toLowerCase();
      if (m.severity === 2) out.counts.errors += 1; else out.counts.warnings += 1;
      if (m.fix && !usedFileFixable) out.counts.autoFixable += 1;
      const rec = { filePath: f.filePath, message: m.message, ruleId: m.ruleId, line: m.line, column: m.column };
      const isMagic = rule.includes('no-redundant-calculations');
      const isComplexity = rule === 'complexity' || rule.includes('prefer-simpler-logic') || rule.includes('no-equivalent-branches') || rule.includes('no-redundant-conditionals');
      const isDomainTerms = rule.includes('enforce-domain-terms') || rule.includes('no-generic-names');
      const isArch = rule.includes('max-lines') || rule.includes('max-lines-per-function') || rule.includes('max-statements') || rule.includes('max-depth') || rule.includes('max-params');
      if (isMagic) push(out, 'magicNumbers', rec);
      else if (isComplexity) push(out, 'complexity', rec);
      else if (isDomainTerms) push(out, 'domainTerms', rec);
      else if (isArch) push(out, 'architecture', rec);
    }
  }
  return out;
}

module.exports = { categorizeViolations };

'use strict';

const path = require('path');
const constantsLib = require(path.join(__dirname, '..', '..', '..', 'lib', 'constants'));

function resolveAllowedDomains(cfg) {
  const raw = (cfg && cfg.domains) ? [cfg.domains.primary].concat(cfg.domains.additional || []) : [];
  const allowed = raw.filter(Boolean);
  const existing = Object.keys(constantsLib.DOMAINS);
  if (allowed.length) return allowed; // honor configured domains even if not in catalog
  return existing; // fallback to all only when nothing configured
}

function buildDomainData(cfg) {
  const priority = Array.isArray(cfg.domainPriority) ? cfg.domainPriority : [];
  const allowed = resolveAllowedDomains(cfg);
  const byDomain = {};
  for (const d of allowed) {
    const mod = constantsLib.getDomain(d);
    if (!mod) continue;
    const constants = Array.isArray(mod.constants) ? mod.constants.slice() : [];
    const terms = Array.isArray(mod.terms) ? mod.terms.map(String) : [];
    byDomain[d] = { constants, terms };
  }
  return { byDomain, priority, allowed };
}

function inferDomainForMessage(message, domainData) {
  if (!message) return null;
  const msg = String(message);

  // search numeric constants first (exact string match)
  for (const [domain, data] of Object.entries(domainData.byDomain)) {
    for (const v of data.constants) {
      if (msg.includes(String(v))) return domain;
    }
  }

  // then look for known terms
  const lower = msg.toLowerCase();
  let bestMatch = null;
  for (const [domain, data] of Object.entries(domainData.byDomain)) {
    for (const term of data.terms) {
      const lowerTerm = String(term).toLowerCase();
      if (lowerTerm && lower.includes(lowerTerm)) {
        if (!bestMatch || lowerTerm.length > bestMatch.len) {
          bestMatch = { domain, len: lowerTerm.length };
        }
      }
    }
  }
  return bestMatch ? bestMatch.domain : null;
}

function attachDomainContext(categories, cfg) {
  const dd = buildDomainData(cfg || {});
  const domainCounts = Object.create(null);
  function annotate(list) {
    for (const rec of list) {
      const d = inferDomainForMessage(rec.message, dd);
      if (d) {
        rec.domain = d; // annotate
        domainCounts[d] = (domainCounts[d] || 0) + 1;
      }
    }
  }
  annotate(categories.magicNumbers || []);
  annotate(categories.domainTerms || []);
  annotate(categories.complexity || []);
  annotate(categories.architecture || []);
  // Build summary restricted to allowed domains, include zero-counts
  const summary = dd.allowed.map((domain) => ({
    domain,
    count: domainCounts[domain] || 0,
    rank: (dd.priority.indexOf(domain) !== -1 ? dd.priority.indexOf(domain) : 999)
  })).sort((a, b) => (b.count - a.count) || (a.rank - b.rank));
  categories.domainSummary = summary;
  return categories;
}

function getDomainHints(categories, cfg) {
  // Build domain data across all known domains to infer hints
  const all = { byDomain: {}, priority: [], allowed: Object.keys(constantsLib.DOMAINS) };
  for (const d of Object.keys(constantsLib.DOMAINS)) {
    const mod = constantsLib.getDomain(d);
    if (!mod) continue;
    all.byDomain[d] = {
      constants: Array.isArray(mod.constants) ? mod.constants.slice() : [],
      terms: Array.isArray(mod.terms) ? mod.terms.map(String) : []
    };
  }
  const counts = Object.create(null);
  function annotate(list) {
    for (const rec of list || []) {
      const d = inferDomainForMessage(rec.message, all);
      if (d) counts[d] = (counts[d] || 0) + 1;
    }
  }
  annotate(categories.magicNumbers || []);
  annotate(categories.domainTerms || []);
  annotate(categories.complexity || []);
  annotate(categories.architecture || []);
  // Exclude configured allowed domains
  const allowed = resolveAllowedDomains(cfg || {});
  const hints = Object.entries(counts)
    .filter(([d]) => !allowed.includes(d))
    .map(([domain, count]) => ({ domain, count }))
    .sort((a, b) => b.count - a.count)
    .slice(0, 5);
  return hints;
}

module.exports = { buildDomainData, inferDomainForMessage, attachDomainContext, getDomainHints };

'use strict';

const fs = require('fs');
const path = require('path');

function round1(x) { return Math.round(x * 10) / 10; }

// Per-rule-type weighting (hours per occurrence)
const RULE_WEIGHTS = new Map([
  // Complexity family
  ['complexity', 1.5],
  ['ai-code-snifftest/prefer-simpler-logic', 1.0],
  ['ai-code-snifftest/no-redundant-conditionals', 0.8],
  ['ai-code-snifftest/no-equivalent-branches', 0.8],
  // Architecture guardrails
  ['max-lines-per-function', 0.25],
  ['max-statements', 0.2],
  ['max-lines', 0.15],
  ['max-depth', 0.15],
  ['max-params', 0.1],
  // Domain terms
  ['ai-code-snifftest/enforce-domain-terms', 0.1],
  ['ai-code-snifftest/no-generic-names', 0.08],
  // Magic numbers
  ['ai-code-snifftest/no-redundant-calculations', 0.05]
]);
const DEFAULT_RULE_WEIGHT = 0.1;

function getFileLinesCount(cwd, filePath) {
  try {
    const p = path.isAbsolute(filePath) ? filePath : path.join(cwd, filePath);
    const content = fs.readFileSync(p, 'utf8');
    return content.split(/\r?\n/).length;
  } catch (_) {
    return null;
  }
}

function fileSizeFactor(lines) {
  if (!Number.isFinite(lines) || lines <= 0) return 1;
  // Scale up gradually, cap at 2x for very large files
  const factor = 1 + Math.min(lines / 1000, 1);
  return factor;
}

function estimateEffort(categories, { cwd = process.cwd(), useFileSize = false } = {}) {
  const c = categories || {};
  const lists = [
    ['magicNumbers', c.magicNumbers || []],
    ['domainTerms', c.domainTerms || []],
    ['architecture', c.architecture || []],
    ['complexity', c.complexity || []]
  ];

  const fileLinesCache = new Map();
  const byCategoryHours = { magicNumbers: 0, domainTerms: 0, architecture: 0, complexity: 0 };
  let totalHours = 0;

  for (const [cat, arr] of lists) {
    for (const rec of arr) {
      const rule = String(rec.ruleId || '').toLowerCase();
      const base = RULE_WEIGHTS.get(rule) ?? DEFAULT_RULE_WEIGHT;
      let weight = base;
      if (useFileSize && rec.filePath) {
        let lines = fileLinesCache.get(rec.filePath);
        if (lines === null || lines === undefined) {
          lines = getFileLinesCount(cwd, rec.filePath);
          fileLinesCache.set(rec.filePath, lines);
        }
        weight *= fileSizeFactor(lines);
      }
      byCategoryHours[cat] += weight;
      totalHours += weight;
    }
  }

  const days = totalHours / 8;
  const weeks = days / 5;
  return {
    hours: round1(totalHours),
    days: round1(days),
    weeks: round1(weeks),
    byCategory: {
      magicNumbers: round1(byCategoryHours.magicNumbers),
      domainTerms: round1(byCategoryHours.domainTerms),
      architecture: round1(byCategoryHours.architecture),
      complexity: round1(byCategoryHours.complexity)
    }
  };
}

module.exports = { estimateEffort };

'use strict';

const fs = require('fs');
const path = require('path');
const { readProjectConfig } = require(path.join(__dirname, '..', '..', 'utils', 'project-config'));
const { categorizeViolations } = require('./categorizer');
const { estimateEffort } = require('./estimator');
const { writeAnalysisReport } = require('./reporter');
const { attachDomainContext } = require('./domain');

function numArg(v, d) { const n = Number(v); return Number.isFinite(n) && n > 0 ? n : d; }

function analyzeCommand(cwd, args) {
  try {
    const input = args._ && args._[1] ? args._[1] : (args.input || 'lint-results.json');
    const outFile = args.output || 'analysis-report.md';
    const format = (args.format || 'markdown').toLowerCase();

    const file = path.isAbsolute(input) ? input : path.join(cwd, input);
    const raw = fs.readFileSync(file, 'utf8');
    const json = JSON.parse(raw);
    const cfg = readProjectConfig({ getFilename: () => path.join(cwd, 'placeholder.js'), getCwd: () => cwd });
    let categories = categorizeViolations(json, cfg);
    categories = attachDomainContext(categories, cfg);

    const useFileSize = String(args['estimate-size'] ?? args.estimateSize ?? 'false').toLowerCase() === 'true';
    const effort = estimateEffort(categories, { cwd, useFileSize });

    const topFiles = numArg(args['top-files'] || args.topFiles, 10);
    const minCount = numArg(args['min-count'] || args.minCount, 1);
    const maxExamples = numArg(args['max-examples'] || args.maxExamples, 5);

    const outPath = path.join(cwd, outFile);
    if (format === 'markdown') {
      writeAnalysisReport(outPath, { categories, effort, cfg, topFiles, minCount, maxExamples });
    } else if (format === 'json') {
      fs.writeFileSync(outPath, JSON.stringify({ categories, effort }, null, 2) + '\n');
    } else if (format === 'html') {
      const md = writeAnalysisReport(null, { categories, effort, cfg, returnString: true });
      const htmlChars = { '&':'&amp;','<':'&lt;','>':'&gt;' };
      const escaped = md.replace(/[&<>]/g, s => htmlChars[s]);
      const html = `<!doctype html><meta charset="utf-8"><title>Analysis Report</title><pre>${escaped}</pre>`;
      fs.writeFileSync(outPath, html);
    }
    return 0;
  } catch (e) {
    console.error(`analyze failed: ${e && e.message}`);
    return 1;
  }
}

module.exports = { analyzeCommand };

'use strict';

const fs = require('fs');
const path = require('path');

function writeAnalysisReport(outPath, { categories, effort, returnString, topFiles = 10, minCount = 1, maxExamples = 5, cfg } = {}) {
  const lines = [];
  lines.push('# Analysis Report');
  lines.push('');
  lines.push(`Errors: ${categories.counts.errors}  Warnings: ${categories.counts.warnings}  Auto-fixable: ${categories.counts.autoFixable}`);
  lines.push('');
  lines.push('## Categories');
  lines.push(`- Magic numbers: ${categories.magicNumbers.length}`);
  lines.push(`- Complexity: ${categories.complexity.length}`);
  lines.push(`- Domain terms: ${categories.domainTerms.length}`);
  lines.push(`- Architecture: ${categories.architecture.length}`);
  lines.push('');

  // Configured domains (from .ai-coding-guide.json)
  const primary = cfg && cfg.domains && cfg.domains.primary;
  const additional = (cfg && cfg.domains && Array.isArray(cfg.domains.additional)) ? cfg.domains.additional : [];
  if (primary || (additional && additional.length)) {
    lines.push('## Configured Domains');
    if (primary) lines.push(`- ${primary} (primary)`);
    for (const d of additional) lines.push(`- ${d} (additional)`);
    lines.push('');

    // Summarize domain-term violations at a high level (no per-domain counts)
    lines.push('### Domain term violations');
    lines.push(`- Total: ${categories.domainTerms.length}`);
    lines.push('- See AGENTS.md for project terminology.');
    lines.push('');
  }

  // Per-category breakdowns
  function byRule(list) {
    const m = new Map();
    for (const r of list || []) {
      const key = String(r.ruleId || 'unknown');
      m.set(key, (m.get(key) || 0) + 1);
    }
    return Array.from(m.entries()).sort((a, b) => b[1] - a[1]);
  }
  function topFilesFn(list, limit = 10, min = 1) {
    const m = new Map();
    for (const r of list || []) {
      const k = String(r.filePath || 'unknown');
      m.set(k, (m.get(k) || 0) + 1);
    }
    return Array.from(m.entries()).filter(([,c]) => c >= min).sort((a,b)=>b[1]-a[1]).slice(0, limit);
  }
  function sample(list, n = 5) { return (list || []).slice(0, n); }
  function readSnippet(filePath, line, context = 2) {
    try {
      const p = path.isAbsolute(filePath) ? filePath : path.join(process.cwd(), filePath);
      const content = fs.readFileSync(p, 'utf8');
      const rows = content.split(/\r?\n/);
      const idx = Math.max(0, (Number(line) || 1) - 1);
      const start = Math.max(0, idx - context);
      const end = Math.min(rows.length, idx + context + 1);
      return rows.slice(start, end).join('\n');
    } catch (_) {
      return null;
    }
  }

  const sections = [
    ['Complexity', categories.complexity],
    ['Architecture', categories.architecture],
    ['Domain terms', categories.domainTerms],
    ['Magic numbers', categories.magicNumbers]
  ];
  for (const [name, list] of sections) {
    lines.push(`## ${name} (${list.length})`);
    const rules = byRule(list);
    if (rules.length) {
      lines.push('### By rule');
      rules.slice(0, 10).forEach(([rule, count]) => lines.push(`- ${rule}: ${count}`));
      lines.push('');
    }
    const files = topFilesFn(list, topFiles, minCount);
    if (files.length) {
      lines.push('### Top files');
      files.forEach(([f, count]) => lines.push(`- ${f}: ${count}`));
      lines.push('');
    }
    const examples = sample(list, maxExamples);
    if (examples.length) {
      lines.push('### Examples');
      examples.forEach(r => {
        lines.push(`- ${r.filePath}:${r.line || 0} ${r.ruleId}  ${r.message}`);
        const snippet = readSnippet(r.filePath, r.line, 2);
        if (snippet) {
          lines.push('');
          lines.push('```js');
          lines.push(snippet);
          lines.push('```');
          lines.push('');
        }
      });
      lines.push('');
    }
  }

  lines.push('## Effort (rough estimate)');
  lines.push(`- Hours: ${effort.hours}`);
  lines.push(`- Days: ${effort.days}`);
  lines.push(`- Weeks: ${effort.weeks}`);
  if (effort.byCategory) {
    lines.push('');
    lines.push('### Effort by category (hours)');
    lines.push(`- Complexity: ${effort.byCategory.complexity}`);
    lines.push(`- Architecture: ${effort.byCategory.architecture}`);
    lines.push(`- Domain terms: ${effort.byCategory.domainTerms}`);
    lines.push(`- Magic numbers: ${effort.byCategory.magicNumbers}`);
  }
  lines.push('');
  lines.push('## Prioritization (impact  effort heuristic)');
  const impact = [
    ['Complexity', categories.complexity.length, effort.byCategory?.complexity || 0],
    ['Architecture', categories.architecture.length, effort.byCategory?.architecture || 0],
    ['Domain terms', categories.domainTerms.length, effort.byCategory?.domainTerms || 0],
    ['Magic numbers', categories.magicNumbers.length, effort.byCategory?.magicNumbers || 0]
  ];
  impact.sort((a, b) => (b[1] - a[1]) || (a[2] - b[2]));
  impact.forEach(([n, c, h]) => lines.push(`- ${n}: count=${c}, est=${h}h`));
  lines.push('');

  lines.push('Note: Domains are constrained to your configuration (domains.primary/additional).');

  const content = lines.join('\n') + '\n';
  if (returnString) return content;
  fs.writeFileSync(outPath, content);
}

module.exports = { writeAnalysisReport };

'use strict';

const path = require('path');
const fs = require('fs');
const { generateIssues, generateInstructions } = require('./markdown');
const { readProjectConfig } = require(path.join(__dirname, '..', '..', 'utils', 'project-config'));

function numArg(v, d) { const n = Number(v); return Number.isFinite(n) && n > 0 ? n : d; }

function tryReadJson(p) { try { return JSON.parse(fs.readFileSync(p, 'utf8')); } catch { return null; } }

function createIssuesCommand(cwd, args) {
  try {
    const input = args._ && args._[1] ? args._[1] : (args.input || 'lint-results.json');
    const outDir = args.output || 'issues';
    const format = (args.format || 'markdown').toLowerCase();
    const includeCmd = (args['include-commands'] || 'github-cli').toLowerCase();
    const labels = args.labels || 'lint,tech-debt';

    if (format !== 'markdown' && format !== 'json') {
      throw new Error(`Unsupported format: ${format}`);
    }

    const file = path.isAbsolute(input) ? input : path.join(cwd, input);
    const json = JSON.parse(fs.readFileSync(file, 'utf8'));
    const cfg = readProjectConfig({ getFilename: () => path.join(cwd, 'placeholder.js'), getCwd: () => cwd });

    const topFiles = numArg(args['top-files'] || args.topFiles, 10);
    const minCount = numArg(args['min-count'] || args.minCount, 1);
    const maxExamples = numArg(args['max-examples'] || args.maxExamples, 5);

    // Optional: attempt to load analysis.json for effort/summary context
    const analysisPath = path.join(cwd, 'analysis.json');
    const analysis = tryReadJson(analysisPath);

    fs.mkdirSync(path.join(cwd, outDir), { recursive: true });
    generateIssues(cwd, outDir, json, { format, cfg, topFiles, minCount, maxExamples, analysis });
    generateInstructions(cwd, outDir, { includeCmd, labels });
    return 0;
  } catch (e) {
    console.error(`create-issues failed: ${e && e.message}`);
    return 1;
  }
}

module.exports = { createIssuesCommand };

'use strict';

const fs = require('fs');
const path = require('path');
const { categorizeViolations } = require('../analyze/categorizer');
const { attachDomainContext, getDomainHints } = require('../analyze/domain');

function w(p, s) { fs.writeFileSync(p, s); }

function clip(n, lo, hi) { return Math.max(lo, Math.min(hi, n)); }

function rel(filePath) {
  try {
    const cwd = process.cwd().replace(/\\+/g, '/');
    const norm = String(filePath || '').replace(/\\+/g, '/');
    return norm.startsWith(cwd + '/') ? norm.slice(cwd.length + 1) : norm;
  } catch { return filePath; }
}

function readSnippet(filePath, line, context = 2) {
  try {
    const p = path.isAbsolute(filePath) ? filePath : path.join(process.cwd(), filePath);
    const content = fs.readFileSync(p, 'utf8');
    const rows = content.split(/\r?\n/);
    const idx = Math.max(0, (Number(line) || 1) - 1);
    const start = Math.max(0, idx - context);
    const end = Math.min(rows.length, idx + context + 1);
    return rows.slice(start, end).join('\n');
  } catch (err) {
    return null;
  }
}

function examplesForDomainTerms(list, limit = 5) {
  const out = [];
  for (const r of list || []) {
    const msg = String(r.message || '');
    let fromTo = null;
    const prefer = msg.match(/Prefer\s+(\w+)\s+over\s+(\w+)/i);
    if (prefer) fromTo = `${prefer[2]}  ${prefer[1]}`;
    const generic = msg.match(/Generic name "([^"]+)"/i);
    if (!fromTo && generic) fromTo = `${generic[1]}  (domain-specific)`;
    if (fromTo) out.push({ text: `- ${fromTo}`, filePath: r.filePath, line: r.line });
    if (out.length >= limit) break;
  }
  return out;
}
function extractNumbersFromMessages(list) {
  const nums = new Map();
  for (const r of list || []) {
    const matches = String(r.message || '').match(/-?\d+(?:\.\d+)?/g);
    if (!matches) continue;
    for (const m of matches) {
      nums.set(m, (nums.get(m) || 0) + 1);
    }
  }
  return Array.from(nums.entries()).sort((a,b)=>b[1]-a[1]).map(([v,c])=>({ value: v, count: c }));
}

function groupByDomain(list) {
  const out = {};
  for (const r of list || []) {
    const d = r.domain || 'unknown';
    if (!out[d]) out[d] = [];
    out[d].push(r);
  }
  return out;
}

function buildMarkdownSections(cats, which /* 'magic'|'terms'|'complexity'|'architecture'|null */, { topFiles = 10, minCount = 1, maxExamples = 5, cfg, analysis } = {}) {
  const lines = [];

  // Configured domains (no counts, no hints when configured)
  const primary = cfg && cfg.domains && cfg.domains.primary;
  const additional = (cfg && cfg.domains && Array.isArray(cfg.domains.additional)) ? cfg.domains.additional : [];
  if (primary || (additional && additional.length)) {
    lines.push('### Configured Domains');
    if (primary) lines.push(`- ${primary} (primary)`);
    for (const d of additional) lines.push(`- ${d} (additional)`);
    lines.push('');
  } else {
    // Only when no configured domains, fall back to detected + hints
    const ds = Array.isArray(cats.domainSummary) ? cats.domainSummary.slice(0, 5) : [];
    const topDomains = ds.map(d => `- ${d.domain}: ${d.count}`).join('\n');
    if (topDomains) {
      lines.push('### Detected Domains');
      lines.push(topDomains);
      lines.push('');
      const allZero = ds.length > 0 && ds.every(d => (d.count || 0) === 0);
      if (allZero) {
        const hints = getDomainHints(cats, cfg) || [];
        if (hints.length) {
          lines.push('### Domain Hints');
          hints.forEach(h => lines.push(`- ${h.domain}: ${h.count}`));
          lines.push('');
        }
      }
    }
  }

  // Summary for selected category
  function pickList() {
    if (which === 'magic') return cats.magicNumbers || [];
    if (which === 'terms') return cats.domainTerms || [];
    if (which === 'complexity') return cats.complexity || [];
    if (which === 'architecture') return cats.architecture || [];
    // null -> aggregate
    return ([]).concat(cats.magicNumbers||[], cats.domainTerms||[], cats.complexity||[], cats.architecture||[]);
  }
  const list = pickList();
  const uniqueFiles = Array.from(new Set(list.map(r => rel(r.filePath))));
  const count = list.length;
  const effortHours = (() => {
    try {
      const byCat = analysis && (analysis.effort?.byCategory || analysis.categories?.effortByCategory || null);
      const key = which === 'magic' ? 'magicNumbers' : which === 'terms' ? 'domainTerms' : which;
      const v = byCat && (byCat[key] || byCat[key] === 0) ? byCat[key] : null;
      return typeof v === 'number' ? v : null;
    } catch { return null; }
  })();
  if (count > 0) {
    lines.push('## Summary');
    lines.push(`${count} violations found across ${uniqueFiles.length} file(s).`);
    if (typeof effortHours === 'number') {
      const days = (effortHours/8).toFixed(1);
      lines.push(`Estimated effort: ${effortHours} hours (~${days} days)`);
    }
    const priority = which === 'complexity' ? 'High' : which === 'terms' ? 'High' : which === 'architecture' ? 'Medium' : 'Low';
    lines.push('');
    lines.push(`**Priority:** ${priority} (${which || 'all'})`);
    lines.push('');
  } else {
    lines.push('## Summary');
    lines.push('0 violations in this category.');
    lines.push('');
  }

  const includeMagic = !which || which === 'magic';
  const includeTerms = !which || which === 'terms';
  const includeComplexity = !which || which === 'complexity';
  const includeArch = !which || which === 'architecture';

  if (includeMagic) {
    const byDom = groupByDomain(cats.magicNumbers);
    const domains = Object.keys(byDom);
    if (domains.length) {
      lines.push('### Suggested Constants by Domain');
      for (const d of domains) {
        const nums = extractNumbersFromMessages(byDom[d]);
        if (!nums.length) continue;
        lines.push(`- ${d}`);
        nums.slice(0, 10).forEach(n => lines.push(`  - ${n.value} (seen ${n.count}x)`));
      }
      lines.push('');
    }
  }

  // Violations breakdown (by rule) for the selected list
  function byRule(listLocal) {
    const m = new Map();
    for (const r of listLocal || []) {
      const key = String(r.ruleId || 'unknown');
      m.set(key, (m.get(key) || 0) + 1);
    }
    return Array.from(m.entries()).sort((a,b)=>b[1]-a[1]);
  }
  if (count) {
    const rules = byRule(list).slice(0,5);
    if (rules.length) {
      lines.push('## Violations Breakdown');
      lines.push('');
      lines.push('### By Rule');
      rules.forEach(([rule, c])=>lines.push(`- \`${rule}\`: ${c} occurrences`));
      lines.push('');
    }
  }

  if (includeTerms) {
    // For domain terms: group by generic name with domain-aware suggestions
    const termList = cats.domainTerms || [];
    if (termList.length) {
      const nameCounts = new Map();
      for (const r of termList) {
        const msg = String(r.message || '');
        const m = msg.match(/Generic name\s+"([^"]+)"/i);
        if (m && m[1]) nameCounts.set(m[1], (nameCounts.get(m[1])||0)+1);
      }
      if (nameCounts.size) {
        lines.push('### By Generic Name');
        const dom = (primary || '').toLowerCase();
        function suggest(name) {
          const s = [];
          if (dom === 'dev-tools' || dom === 'devtools' || dom === 'dev_tools') {
            if (name==='result') s.push('ruleResult','validationResult','parseResult');
            if (name==='arr') s.push('violations','rules','constants');
            if (name==='list') s.push('violationList','ruleList','constantList');
            if (name==='data') s.push('ruleData','configData','parserData');
          }
          if (dom === 'cli') {
            if (name==='result') s.push('commandResult','executionResult');
            if (name==='arr') s.push('commands','arguments','options');
            if (name==='list') s.push('commandList','argumentList');
          }
          if (dom === 'linting') {
            if (name==='result') s.push('lintResult','analysisResult');
            if (name==='arr') s.push('violations','warnings','errors');
            if (name==='list') s.push('violationList','warningList');
          }
          return s;
        }
        const sorted = Array.from(nameCounts.entries()).sort((a,b)=>b[1]-a[1]).slice(0,5);
        for (const [n,cnt] of sorted) {
          const sug = suggest(n);
          const suffix = sug.length ? `  ${sug.join(', ')}` : '';
          lines.push(`- \`${n}\`: ${cnt} occurrences${suffix}`);
        }
        lines.push('');
      }
    }
    const examples = examplesForDomainTerms(cats.domainTerms, Math.min(5, maxExamples || 5));
    if (examples.length) {
      lines.push('### Examples');
      for (const ex of examples) {
        lines.push(ex.text);
        const snip = readSnippet(ex.filePath, ex.line, 2);
        if (snip) {
          lines.push('');
          lines.push('```js');
          lines.push(snip);
          lines.push('```');
          lines.push('');
        }
      }
    }
  }

  // Top files affected for selected category, with first 3 violations per file
  if (count) {
    const m = new Map();
    for (const r of list) {
      const k = rel(r.filePath);
      const fileViolations = m.get(k) || [];
      fileViolations.push(r);
      m.set(k, fileViolations);
    }
    const top = Array.from(m.entries()).map(([f, fileViolations])=>[f, fileViolations]).sort((a,b)=>b[1].length - a[1].length).slice(0, Math.min(10, topFiles||10));
    if (top.length) {
      lines.push('## Top Files Affected');
      let i = 1;
      for (const [f, fileViolations] of top) {
        lines.push(`${i}. \`${f}\`: ${fileViolations.length} violations`);
        const detail = fileViolations.slice(0,3).map(v=>`   - Line ${v.line || 0}: ${v.message}`).join('\n');
        if (detail) lines.push(detail);
        lines.push('');
        i++;
      }
    }
  }

  if (includeComplexity) {
    const files = new Map();
    (cats.complexity || []).forEach(r => files.set(r.filePath, (files.get(r.filePath) || 0) + 1));
    const top = Array.from(files.entries()).filter(([,c]) => c >= minCount).sort((a,b)=>b[1]-a[1]).slice(0, clip(topFiles,1,50));
    if (top.length) {
      lines.push('### Complexity Hotspots (top files)');
      top.forEach(([f, c]) => lines.push(`- ${f}: ${c}`));
      lines.push('');
    }
    const ex = (cats.complexity || []).slice(0, Math.min(5, maxExamples || 5));
    if (ex.length) {
      lines.push('### Examples');
      for (const r of ex) {
        lines.push(`- ${rel(r.filePath)}:${r.line || 0} ${r.ruleId}  ${r.message}`);
        const code = r.source || readSnippet(r.filePath, r.line, 2);
        if (code) {
          lines.push('');
          lines.push('```js');
          lines.push(code);
          lines.push('```');
          lines.push('');
        }
      }
      // Category-specific suggestion
      lines.push('Suggested Fix: Extract helpers, use guard clauses, and prefer a command map to reduce branching.');
      lines.push('');
    }
  }

  if (includeArch) {
    const files = new Map();
    (cats.architecture || []).forEach(r => files.set(r.filePath, (files.get(r.filePath) || 0) + 1));
    const top = Array.from(files.entries()).filter(([,c]) => c >= minCount).sort((a,b)=>b[1]-a[1]).slice(0, clip(topFiles,1,50));
    if (top.length) {
      lines.push('### Architecture Hotspots (top files)');
      top.forEach(([f, c]) => lines.push(`- ${f}: ${c}`));
      lines.push('');
    }
    const ex = (cats.architecture || []).slice(0, Math.min(5, maxExamples || 5));
    if (ex.length) {
      lines.push('### Examples');
      for (const r of ex) {
        lines.push(`- ${rel(r.filePath)}:${r.line || 0} ${r.ruleId}  ${r.message}`);
        const code = r.source || readSnippet(r.filePath, r.line, 2);
        if (code) {
          lines.push('');
          lines.push('```js');
          lines.push(code);
          lines.push('```');
          lines.push('');
        }
      }
      lines.push('Suggested Fix: Split monolithic functions/files; enforce limits from .ai-coding-guide.json.');
      lines.push('');
    }
  }

  // Fix Strategy (category-specific)
  lines.push('## Fix Strategy');
  if (which === 'magic') {
    lines.push('- Extract repeated numeric literals into named constants.');
    lines.push('- Centralize common units/time/geometry values.');
  } else if (which === 'terms') {
    lines.push('- Replace generic names with domain-appropriate terms (see suggestions above).');
    lines.push('- Apply changes consistently per file to avoid mixed terminology.');
  } else if (which === 'complexity') {
    lines.push('- Use orchestration shells and strategy maps to reduce branching.');
    lines.push('- Extract helpers from long functions; use guard clauses.');
  } else if (which === 'architecture') {
    lines.push('- Enforce function/file limits from .ai-coding-guide.json.');
    lines.push('- Split by feature; keep orchestrators thin.');
  }
  lines.push('');

  // Verification
  lines.push('## Verification');
  lines.push('```bash');
  lines.push('npx eslint . --format json > lint-results.json');
  lines.push('npx eslint-plugin-ai-code-snifftest analyze --input=lint-results.json');
  lines.push('```');
  lines.push('');

  // Acceptance Criteria (specific)
  const targetDown = count ? Math.max(0, Math.ceil(count*0.2)) : 0;
  lines.push('## Acceptance Criteria');
  lines.push(`- [ ] Violations reduced: ${count}  ${targetDown} (or 0)`);
  lines.push('- [ ] Top 5 files addressed');
  lines.push('- [ ] Tests green: npm test');
  lines.push('- [ ] Patterns in AGENTS.md followed');
  lines.push('');
  return lines.join('\n');
}

function writeMarkdownIssues(outPath, cats, opts = {}) {
  function section(title, preface, which) {
    const parts = [];
    parts.push(`# ${title}`);
    parts.push('');
    if (preface) { parts.push(preface); parts.push(''); }
    parts.push(buildMarkdownSections(cats, which, opts));
    return parts.join('\n');
  }
  const files = [
    { name: '01-phase1-magic-numbers.md', content: section('[Phase 1] Fix Magic Numbers', 'Replace repeated numeric literals with named constants.', 'magic') },
    { name: '02-phase1-auto-fix.md', content: section('[Phase 1] Auto-fix Sweep', 'Run ESLint auto-fix to resolve straightforward issues (quotes, formatting, etc.).\n\n```bash\nnpx eslint --fix .\n```\n', null) },
    { name: '03-phase2-domain-terms.md', content: section('[Phase 2] Improve Domain Terms', 'Align terminology with configured domains.', 'terms') },
    { name: '04-phase3-complexity.md', content: section('[Phase 3] Reduce Complexity', 'Refactor complex functions and simplify logic.', 'complexity') },
    { name: '05-phase4-architecture.md', content: section('[Phase 4] Architecture Polish', 'Ensure file/function/params limits are respected.', 'architecture') }
  ];
  files.forEach(f => w(outPath(f.name), f.content));
}

function writeJsonIndex(outPath, cats) {
  const byDom = groupByDomain(cats.magicNumbers);
  const suggestions = {};
  for (const [domain, list] of Object.entries(byDom)) {
    suggestions[domain] = { constants: extractNumbersFromMessages(list) };
  }
  const payload = {
    domainSummary: cats.domainSummary || [],
    suggestions,
    counts: cats.counts
  };
  fs.writeFileSync(outPath('index.json'), JSON.stringify(payload, null, 2) + '\n');
}

function generateIssues(cwd, outDir, eslintJson /* array */, opts = {}) {
  const outPath = (name) => path.join(cwd, outDir, name);
  const cfg = opts.cfg || {};
  let cats = categorizeViolations(eslintJson, cfg);
  cats = attachDomainContext(cats, cfg);
  if (opts.format === 'json') {
    writeJsonIndex(outPath, cats);
    return;
  }
  writeMarkdownIssues(outPath, cats, { topFiles: opts.topFiles, minCount: opts.minCount, maxExamples: opts.maxExamples, cfg, analysis: opts.analysis });
}

function generateInstructions(cwd, outDir, { includeCmd = 'github-cli', labels = 'lint,tech-debt' } = {}) {
  const p = path.join(cwd, outDir, '00-README.md');
  const lines = [];
  lines.push('# How to Create Issues from These Files');
  lines.push('');
  lines.push('This directory contains issue files generated from ESLint analysis. It deliberately does not create tracker issues via API.');
  lines.push('');
  lines.push('## Option 1: Create Manually');
  lines.push('Copy each .md into your tracker as a new issue, edit, then submit.');
  lines.push('');
  if (includeCmd === 'github-cli') {
    lines.push('## Option 2: GitHub CLI (Bulk)');
    lines.push('```bash');
    lines.push('for file in issues/*.md; do');
    lines.push('  if [ "$file" = "issues/00-README.md" ]; then continue; fi');
    lines.push('  title=$(head -n1 "$file" | sed "s/^# //")');
    lines.push(`  gh issue create --title "$title" --body-file "$file" --label "${labels}"`);
    lines.push('done');
    lines.push('```');
  } else if (includeCmd === 'gitlab-cli') {
    lines.push('## Option 2: GitLab CLI (Bulk)');
    lines.push('```bash');
    lines.push('for file in issues/*.md; do');
    lines.push('  if [ "$file" = "issues/00-README.md" ]; then continue; fi');
    lines.push('  title=$(head -n1 "$file" | sed "s/^# //")');
    lines.push(`  glab issue create --title "$title" --description "$(cat $file)" --label "${labels}"`);
    lines.push('done');
    lines.push('```');
  }
  w(p, lines.join('\n') + '\n');
}

module.exports = { generateIssues, generateInstructions };

