// Generators
const { writeConfig } = require(path.join(__dirname, '..', '..', 'generators', 'config-json'));
const { writeAgentsMd } = require(path.join(__dirname, '..', '..', 'generators', 'agents-md'));
const { writeCursorRules } = require(path.join(__dirname, '..', '..', 'generators', 'cursorrules'));
const { writeEslintConfig } = require(path.join(__dirname, '..', '..', 'generators', 'eslint-config'));

// Utilities
const { applyFingerprintToConfig } = require(path.join(__dirname, '..', '..', 'utils', 'fingerprint'));
const { suggestFor } = require(path.join(__dirname, '..', '..', 'utils', 'domain-suggestions'));
const { ask } = require(path.join(__dirname, '..', '..', 'utils', 'readline-utils'));
const { loadProjectConfigFile, deepMerge } = require(path.join(__dirname, '..', '..', 'utils', 'project-config'));
const { shouldEnableArchitecture, normalizeBoolean } = require(path.join(__dirname, '..', '..', 'utils', 'arch-switch'));
const { shouldEnableExternalConstants } = require(path.join(__dirname, '..', '..', 'utils', 'external-switch'));

function initCommand(cwd, args) {
  // Load existing config to preserve data from learn command
  const { json: existingCfg } = loadProjectConfigFile(cwd);

  const primary = (args.primary || 'general').trim();
  const additional = (args.additional || '').split(',').map(s => s.trim()).filter(Boolean);
  const domainPriority = [primary, ...additional];
const external = shouldEnableExternalConstants(args);
  const allowlist = (args.allowlist || '').split(',').map(s=>s.trim()).filter(Boolean);
  const minimumMatch = args.minimumMatch ? parseFloat(args.minimumMatch) : 0.6;
  const minimumConfidence = args.minimumConfidence ? parseFloat(args.minimumConfidence) : 0.7;

  // Create new config structure
  // Note: Don't include antiPatterns here to preserve existing forbiddenNames from learn
  const newCfg = {
    domains: { primary, additional },
    domainPriority,
    constants: {},
    terms: { entities: [], properties: [], actions: [] },
    naming: { style: 'camelCase', booleanPrefix: ['is','has','should','can'], asyncPrefix: ['fetch','load','save'], pluralizeCollections: true },
    minimumMatch,
    minimumConfidence,
    experimentalExternalConstants: external,
    externalConstantsAllowlist: allowlist
  };

  // Merge with existing config to preserve learn command data
  const cfg = deepMerge(existingCfg, newCfg);
// Add architecture guardrails by default (disable with --no-arch or --arch=false)
  const enableArch = shouldEnableArchitecture(args);
  if (enableArch) {
    const { DEFAULT_ARCHITECTURE } = require(path.join(__dirname, '..', '..', 'utils', 'arch-defaults'));
    cfg.architecture = JSON.parse(JSON.stringify(DEFAULT_ARCHITECTURE));
  } else {
    if (cfg && Object.prototype.hasOwnProperty.call(cfg, 'architecture')) delete cfg.architecture;
  }
  // Merge fingerprint signals into config (domains + constantResolution)
  applyFingerprintToConfig(cwd, cfg);
  if (external && (!allowlist || allowlist.length === 0)) {
    console.warn('Warning: --external used without allowlist; consider adding --allowlist to limit npm scope.');
  }
  const code = writeConfig(cwd, cfg);
  const hasWarp = fs.existsSync(path.join(cwd, 'WARP.md'));
  // Cursor rules are opt-in only (do not enable with --yes)
  if (args.cursor) writeCursorRules(cwd, cfg);

  // Defaults: generate AGENTS.md and ESLint config unless explicitly opted out
  const disableAgents = Object.prototype.hasOwnProperty.call(args, 'no-agents') && normalizeBoolean(args['no-agents'], true);
  const disableEslint = Object.prototype.hasOwnProperty.call(args, 'no-eslint') && normalizeBoolean(args['no-eslint'], true);
  const explicitAgents = Object.prototype.hasOwnProperty.call(args, 'agents') ? normalizeBoolean(args.agents, true) : undefined;
  const explicitEslint = Object.prototype.hasOwnProperty.call(args, 'eslint') ? normalizeBoolean(args.eslint, true) : undefined;
  const shouldWriteAgents = disableAgents ? false : (explicitAgents === undefined ? true : explicitAgents);
  const shouldWriteEslint = disableEslint ? false : (explicitEslint === undefined ? true : explicitEslint);

  // Informative note: --agents/--eslint are defaults now
  if (args.agents && !disableAgents) {
    console.log('Note: --agents is the default; this flag is no longer required.');
  }
  if (args.eslint && !disableEslint) {
    console.log('Note: --eslint is the default; this flag is no longer required.');
  }

  if (shouldWriteAgents) {
    writeAgentsMd(cwd, cfg);
    if (hasWarp) {
      console.log('Found WARP.md  preserving it; generated AGENTS.md alongside.');
    }
  }
  if (shouldWriteEslint) writeEslintConfig(cwd, cfg);

  // Post-init guidance
  console.log('\nProject initialized.');
  printRatchetNextSteps();

  // Optional debug snapshot to aid CI investigation
  try {
    if (args.debug || process.env.AI_DEBUG_INIT) {
      const debugFile = path.join(cwd, '.ai-init-debug.json');
      const eslintFileJs = path.join(cwd, 'eslint.config.js');
      const eslintFileMjs = path.join(cwd, 'eslint.config.mjs');
      const eslintFile = fs.existsSync(eslintFileMjs) ? eslintFileMjs : (fs.existsSync(eslintFileJs) ? eslintFileJs : null);
      const eslintContent = eslintFile && fs.existsSync(eslintFile) ? fs.readFileSync(eslintFile, 'utf8') : '';
      const agentsFile = path.join(cwd, 'AGENTS.md');
      const agentsContent = fs.existsSync(agentsFile) ? fs.readFileSync(agentsFile, 'utf8') : '';
      const snapshot = {
        args: {
          yes: !!args.yes,
          eslint: normalizeBoolean(args.eslint, true),
          noEslint: args['no-eslint'],
          agents: normalizeBoolean(args.agents, true),
          noAgents: args['no-agents'],
          noArch: args['no-arch'],
          arch: args.arch
        },
        argsRaw: args,
        enableArchVar: enableArch,
        enableArch: Boolean(!args['no-arch'] && args.arch && args.arch !== 'false'),
        cfgHasArchitecture: !!cfg.architecture,
        files: {
          eslintConfig: eslintFile,
          eslintHasGuardrails: Boolean(eslintContent && (/Architecture guardrails/.test(eslintContent) || /'max-lines'/.test(eslintContent))),
          agentsMd: fs.existsSync(agentsFile),
          agentsHasArchitectureSection: /## Architecture Guidelines/.test(agentsContent)
        },
        willWrite: {
          eslint: shouldWriteEslint,
          agents: shouldWriteAgents,
          cursor: !!args.cursor
        }
      };
      fs.writeFileSync(debugFile, JSON.stringify(snapshot, null, 2));
    }
  } catch { /* ignore debug write errors */ }

  return code;
}

async function initInteractiveCommand(cwd, args) {
  const readline = require('readline');
  const rl = readline.createInterface({ input: process.stdin, output: process.stdout });
  try {
    let primary = (await ask(rl, 'Primary domain (default: general): ')).trim() || 'general';
    const suggested = suggestFor(primary);
    if (suggested.length) {
      console.log(`Suggested additional domains for ${primary}: ${suggested.join(', ')}`);
    }
    // Show domain metadata via wizard helper
    try {
      const { buildDomainMetadata } = require(path.join(__dirname, '..', '..', 'wizard', 'domain-selector'));
      const metas = buildDomainMetadata();
      if (Array.isArray(metas) && metas.length) {
        console.log('\nDiscovered domains:');
        for (const m of metas) {
          const src = m.sources && m.sources.length ? m.sources.join(', ') : 'internal';
          console.log(`  - ${m.name} (constants: ${m.constantsCount}, terms: ${m.termsCount}, sources: ${src})`);
        }
        const sel = metas.find(d => d.name === primary);
        if (sel && sel.constantsCount === 0) {
          console.warn(` Warning: selected primary '${primary}' has zero discovered constants.`);
        }
      }
    } catch { /* ignore wizard metadata errors */ }
    let addAns = (await ask(rl, 'Additional domains (comma-separated, optional): ')).trim();

    // Optional: interactive external discovery listing
    // Keep interactive external discovery prompts gated behind an explicit flag to avoid changing prompt flow by default
    const externalPromptEnabled = (args && (Object.prototype.hasOwnProperty.call(args, 'external') || Object.prototype.hasOwnProperty.call(args, 'experimentalExternalConstants')))
      && shouldEnableExternalConstants(args || {});
    if (externalPromptEnabled) {
      try {
        const { discoverConstants } = require(path.join(__dirname, '..', '..', 'utils', 'discover-constants'));
        const { mergeConstants } = require(path.join(__dirname, '..', '..', 'utils', 'merge-constants'));
        const discovered = discoverConstants(cwd);
        const merged = mergeConstants(discovered);
        const sourcesByDomain = {};
        for (const [d, data] of Object.entries(merged || {})) {
          const srcs = (data.sources || []).map(s => s.type);
          sourcesByDomain[d] = Array.from(new Set(srcs)).join(',');
        }
        const domainList = Object.keys(sourcesByDomain).sort();
        if (domainList.length) {
          console.log('\nDiscovered domains (experimental):');
          domainList.forEach((d, i) => console.log(`  [${i+1}] ${d} (${sourcesByDomain[d]})`));
          const priPick = (await ask(rl, 'Pick primary by name or index (Enter to keep): ')).trim();
          if (priPick) {
            const idx = Number(priPick);
            if (Number.isInteger(idx) && idx>=1 && idx<=domainList.length) {
              primary = domainList[idx-1];
            } else if (domainList.includes(priPick)) {
              primary = priPick;
            }
          }
          const addPick = (await ask(rl, 'Pick additional (comma-separated names or indices, Enter to keep): ')).trim();
          if (addPick) {
            const tokens = addPick.split(',').map(s=>s.trim()).filter(Boolean);
            const chosen = [];
            for (const t of tokens) {
              const n = Number(t);
              if (Number.isInteger(n) && n>=1 && n<=domainList.length) chosen.push(domainList[n-1]);
              else if (domainList.includes(t)) chosen.push(t);
            }
            if (chosen.length) addAns = chosen.join(',');
          }
        }
      } catch (err) {
        console.warn(`(external discovery skipped: ${err && err.message})`);
      }
    }
    const additional = addAns ? addAns.split(',').map(s=>s.trim()).filter(Boolean) : [];
    const domainPriority = [primary, ...additional];
    console.log(`\nSummary:\n  primary: ${primary}\n  additional: ${additional.join(', ') || '(none)'}\n  domainPriority: ${domainPriority.join(', ')}`);

    // Architecture guardrails prompt
    const { promptArchitectureGuardrails } = require(path.join(__dirname, '..', '..', 'wizard', 'arch-prompts'));
    const architecture = await promptArchitectureGuardrails(rl, ask);

    const confirm = (await ask(rl, '\nWrite .ai-coding-guide.json with these settings? (Y/n): ')).trim().toLowerCase();
    if (confirm && confirm.startsWith('n')) {
      console.log('Aborted.');
      return 1;
    }

    // Load existing config to preserve data from learn command
    const { json: existingCfg } = loadProjectConfigFile(cwd);

    // Create new config structure
    // Note: Don't include antiPatterns here to preserve existing forbiddenNames from learn
    const newCfg = {
      domains: { primary, additional },
      domainPriority,
      constants: {},
      terms: { entities: [], properties: [], actions: [] },
      naming: { style: 'camelCase', booleanPrefix: ['is','has','should','can'], asyncPrefix: ['fetch','load','save'], pluralizeCollections: true }
    };

    // Merge with existing config to preserve learn command data
    const cfg = deepMerge(existingCfg, newCfg);
    if (architecture) {
      cfg.architecture = architecture;
    }
    const code = writeConfig(cwd, cfg);
    const genCursor = (await ask(rl, 'Generate .cursorrules? (Y/n): ')).trim().toLowerCase();
    if (!genCursor || genCursor.startsWith('y')) {
      writeCursorRules(cwd, cfg);
    }
    const hasWarp = fs.existsSync(path.join(cwd, 'WARP.md'));
    console.log(hasWarp ? 'Detected WARP.md  will not modify it.' : 'No WARP.md detected.');
    const agents = (await ask(rl, 'Generate AGENTS.md (recommended)? (Y/n): ')).trim().toLowerCase();
    if (!agents || agents.startsWith('y')) {
      writeAgentsMd(cwd, cfg);
    }
    const genEslint = (await ask(rl, 'Generate eslint.config.mjs (Y/n): ')).trim().toLowerCase();
    if (!genEslint || genEslint.startsWith('y')) {
      writeEslintConfig(cwd, cfg);
    }
    console.log('\nProject initialized.');
    printRatchetNextSteps();
    return code;
  } finally {
    rl.close();
  }
}

module.exports = { initCommand, initInteractiveCommand };

'use strict';

const fs = require('fs');
const path = require('path');

const { deepMerge, loadProjectConfigFile, writeProjectConfigFile } = require(path.join(__dirname, '..', '..', 'utils', 'project-config'));
const { shouldEnableArchitecture, normalizeBoolean } = require(path.join(__dirname, '..', '..', 'utils', 'arch-switch'));
const { writeAgentsMd } = require(path.join(__dirname, '..', '..', 'generators', 'agents-md'));
const { writeEslintConfig } = require(path.join(__dirname, '..', '..', 'generators', 'eslint-config'));
const { detectProjectContext } = require(path.join(__dirname, '..', '..', 'utils', 'project-detection'));

async function learnInteractiveCommand(cwd, args, findings, rec, currentCfg) {
  const readline = require('readline');
  const rl = readline.createInterface({ input: process.stdin, output: process.stdout });
  const ask = (q) => new Promise((resolve)=> rl.question(q, (ans)=> resolve(ans)));
  try {
    const nextCfg = JSON.parse(JSON.stringify(currentCfg));
    console.log('\nLearn: Reconciliation Report');
    console.log(`Score: ${rec.score.overall}/100`);

    // Phase 3: suggest inferred domain
    try {
      const ctx = detectProjectContext(cwd);
      const inferred = ctx && ctx.type === 'dev-tools' ? 'dev-tools'
        : ctx && ctx.type === 'cli' ? 'cli'
        : ctx && ctx.type === 'web-app' ? 'web-app'
        : null;
      if (inferred) {
        const pick = (await ask(`\nDetected domain: ${inferred}. Use this as primary? (Y/n): `)).trim().toLowerCase();
        if (!pick || pick.startsWith('y')) {
          nextCfg.domains = nextCfg.domains || { primary: 'general', additional: [] };
          nextCfg.domains.primary = inferred;
          const addl = Array.isArray(nextCfg.domains.additional) ? nextCfg.domains.additional : [];
          nextCfg.domainPriority = [inferred, ...addl].filter(Boolean);
        }
      }
    } catch { /* ignore detection errors */ }
    if (rec.score && rec.score.breakdown) {
      console.log('Breakdown:', rec.score.breakdown);
    }
    if (Array.isArray(rec.warnings) && rec.warnings.length) {
      console.log('Warnings:', rec.warnings);
    }

    // 1) Naming style
    console.log(`\nNaming style: suggested '${rec.result.naming.style}' (majorities: ${Object.entries(findings.naming.casing).map(([k,v])=>`${k}:${v}`).join(', ')})`);
    let ans = (await ask('[Naming] Enforce suggested style in config? [Y/n/r] (r=view report): ')).trim().toLowerCase();
    if (ans === 'r') {
      console.log(JSON.stringify({ casing: findings.naming.casing, booleanPrefixes: findings.naming.booleanPrefixes }, null, 2));
      ans = (await ask('Apply suggested style? [Y/n]: ')).trim().toLowerCase();
    }
    if (!ans || ans.startsWith('y')) {
      nextCfg.naming = nextCfg.naming || {};
      nextCfg.naming.style = rec.result.naming.style;
      const bp = Array.from(new Set(rec.result.naming.booleanPrefix || []));
      // Offer inline edit of boolean prefixes
      const edit = (await ask(`Edit boolean prefixes? current=[${bp.join(',')}] enter new comma-separated or press Enter to keep: `)).trim();
      if (edit) {
        const edited = edit.split(',').map(s=>s.trim()).filter(Boolean);
        nextCfg.naming.booleanPrefix = edited.length ? edited : bp;
      } else {
        nextCfg.naming.booleanPrefix = bp;
      }
    }

    // 2) Generic names  antiPatterns.forbiddenNames
    const forb = rec.result.antiPatterns && rec.result.antiPatterns.forbiddenNames || [];
    if (forb.length) {
      console.log(`\nGeneric names detected (suggest forbid): ${forb.join(', ')}`);
      const g = (await ask('[Generic] Add to antiPatterns.forbiddenNames? [Y/n]: ')).trim().toLowerCase();
      if (!g || g.startsWith('y')) {
        nextCfg.antiPatterns = nextCfg.antiPatterns || { forbiddenNames: [], forbiddenTerms: [] };
        const set = new Set([...(nextCfg.antiPatterns.forbiddenNames||[]), ...forb]);
        nextCfg.antiPatterns.forbiddenNames = Array.from(set);
      }
    }

    // 3) Constants domain-aware suggestions
    const consts = (rec.domain && rec.domain.constants) || [];
    const top = consts.slice(0, 10);
    if (top.length) {
      console.log('\nDomain-aware constants (high-confidence):');
      const chosen = [];
      const history = [];
      for (let i = 0; i < top.length; i++) {
        const c = top[i];
        let action = (await ask(`  [${i+1}/${top.length}] ${c.value}  ${c.suggestedName || '(name?)'} ${c.domain ? '['+c.domain+']' : ''} (conf=${Math.round(c.confidence*100)}%) [a]dd, [r]ename, [m]ap, [s]kip, [b]ack, skip-[A]ll, [q]uit: `)).trim().toLowerCase();
        if (action === 'q') break;
        if (action === 'a' && i < top.length - 1) {
          console.log('Skipping all remaining constants.');
          break;
        }
        if (action === 'b' && i > 0) {
          // Go back one step
          i = Math.max(0, i - 2);
          if (history.length > 0) {
            const last = history.pop();
            const idx = chosen.findIndex(ch => ch.value === last.value);
            if (idx !== -1) chosen.splice(idx, 1);
          }
          continue;
        }
        if (action === 'r') {
          const nn = (await ask('   New constant name (UPPER_SNAKE_CASE): ')).trim();
          if (nn) c.suggestedName = nn;
          action = 'a';
        }
        if (action === 'm') {
          const dm = (await ask('   Map value to domain (e.g., time, astronomy, geometry): ')).trim();
          if (dm) {
            nextCfg.constantResolution = nextCfg.constantResolution || {};
            nextCfg.constantResolution[String(c.value)] = dm;
            c.domain = dm;
          }
          const yn = (await ask('   Also add to fingerprint? [Y/n]: ')).trim().toLowerCase();
          if (!yn || yn.startsWith('y')) action = 'a'; else action = 's';
        }
        if (action === 'a') {
          chosen.push({ value: c.value, suggestedName: c.suggestedName, domain: c.domain, confidence: c.confidence });
          history.push(c);
        }
      }
      if (chosen.length) {
        const h = (await ask('[Constants] Generate fingerprint file with selected items? [Y/n]: ')).trim().toLowerCase();
        if (!h || h.startsWith('y')) {
          const { generateDomain } = require(path.join(__dirname, '..', '..', 'scanner', 'reconcile'));
          const content = generateDomain({ constants: chosen });
          const outDir = path.join(cwd, '.ai-constants');
          fs.mkdirSync(outDir, { recursive: true });
          const outFile = path.join(outDir, 'project-fingerprint.js');
          fs.writeFileSync(outFile, content);
          console.log(`Wrote ${outFile}`);
        }
      }
    }

    // 4) Apply config changes
    const apply = (await ask('\nApply changes to .ai-coding-guide.json? [Y/n]: ')).trim().toLowerCase();
    if (!apply || apply.startsWith('y')) {
      const { file } = loadProjectConfigFile(cwd);
      writeProjectConfigFile(file, nextCfg);
      console.log(`Updated ${file}`);
    } else {
      console.log('Skipped writing config.');
    }

    // Phase 2.3: Offer to generate AGENTS.md and ESLint config now
    const gen = (await ask('\nGenerate AGENTS.md and eslint.config.mjs now? (Y/n): ')).trim().toLowerCase();
    if (!gen || gen.startsWith('y')) {
      // Determine default-on outputs with opt-outs
      const disableAgents = Object.prototype.hasOwnProperty.call(args, 'no-agents') && normalizeBoolean(args['no-agents'], true);
      const disableEslint = Object.prototype.hasOwnProperty.call(args, 'no-eslint') && normalizeBoolean(args['no-eslint'], true);
      const shouldWriteAgents = !disableAgents;
      const shouldWriteEslint = !disableEslint;

      // Load latest config from disk
      const { file, json: cfgLatest } = loadProjectConfigFile(cwd);

      // Ensure architecture defaults (enabled by default unless disabled)
      const enableArch = shouldEnableArchitecture(args);
      if (enableArch && !cfgLatest.architecture) {
        try {
          const { DEFAULT_ARCHITECTURE } = require(path.join(__dirname, '..', '..', 'utils', 'arch-defaults'));
          cfgLatest.architecture = JSON.parse(JSON.stringify(DEFAULT_ARCHITECTURE));
          writeProjectConfigFile(file, cfgLatest);
          console.log('Updated config with architecture guardrails.');
        } catch (e) {
          console.warn(`Warning: could not enable architecture guardrails: ${e && e.message}`);
        }
      } else if (!enableArch && cfgLatest.architecture) {
        // Respect explicit disable
        delete cfgLatest.architecture;
        writeProjectConfigFile(file, cfgLatest);
        console.log('Removed architecture guardrails from config.');
      }

      // Write outputs
      if (shouldWriteAgents) writeAgentsMd(cwd, cfgLatest);
      if (shouldWriteEslint) writeEslintConfig(cwd, cfgLatest);
    }

    return 0;
  } finally {
    rl.close();
  }
}

function learnCommand(cwd, args) {
  const { scanProject } = require(path.join(__dirname, '..', '..', 'scanner', 'extract'));
  const { reconcile, DEFAULT_SANITY } = require(path.join(__dirname, '..', '..', 'scanner', 'reconcile'));
  const { json: currentCfg } = loadProjectConfigFile(cwd);
  const modeInteractive = Boolean(args.interactive) || (!args.strict && !args.permissive && process.stdin.isTTY);
  const sample = args.sample ? Number(args.sample) : undefined;
  const useCache = !!args.cache || args['no-cache'];

  console.log(`Scanning project (sample: ${sample || 400}, cache: ${useCache ? 'enabled' : 'disabled'})...`);
  const findings = scanProject(cwd, { sample: sample || 400, useCache });
  console.log(' Scan complete');
  // Override defaults with CLI flags or config values
  const sane = { ...DEFAULT_SANITY };
  if (args.minimumMatch) sane.minimumMatch = parseFloat(args.minimumMatch);
  else if (currentCfg.minimumMatch !== undefined) sane.minimumMatch = currentCfg.minimumMatch;
  if (args.minimumConfidence) sane.minimumConfidence = parseFloat(args.minimumConfidence);
  else if (currentCfg.minimumConfidence !== undefined) sane.minimumConfidence = currentCfg.minimumConfidence;
  const mode = args.strict ? 'strict' : (args.permissive ? 'permissive' : 'adaptive');
  const rec = reconcile(findings, sane, { config: currentCfg, mode });

  if (modeInteractive) {
    return learnInteractiveCommand(cwd, args, findings, rec, currentCfg);
  }

  // Non-interactive: compute result and optionally apply
  const nextCfg = deepMerge(currentCfg, { naming: rec.result.naming, antiPatterns: rec.result.antiPatterns, constantResolution: rec.result.constantResolution });
  if (args.apply) {
    if (mode === 'permissive') {
      const reportFile = path.join(cwd, '.ai-learn-report.json');
      fs.writeFileSync(reportFile, JSON.stringify({ mode, score: rec.score, result: rec.result, warnings: rec.warnings, domain: rec.domain.summary }, null, 2) + '\n');
      console.log(`Wrote ${reportFile}`);
    } else {
      const { file } = loadProjectConfigFile(cwd);
      writeProjectConfigFile(file, nextCfg);
      console.log(`Updated ${file} (mode ${mode}, score ${rec.score.overall})`);
    }
  } else {
    console.log(JSON.stringify({ mode, score: rec.score, result: rec.result, warnings: rec.warnings, domain: rec.domain.summary }, null, 2));
  }
  if (args.fingerprint) {
    const top = (rec.domain && rec.domain.constants || []).slice(0, 10);
    if (top.length) {
      const { generateDomain } = require(path.join(__dirname, '..', '..', 'scanner', 'reconcile'));
      const content = generateDomain({ constants: top });
      const outDir = path.join(cwd, '.ai-constants');
      fs.mkdirSync(outDir, { recursive: true });
      const outFile = path.join(outDir, 'project-fingerprint.js');
      fs.writeFileSync(outFile, content);
      console.log(`Wrote ${outFile}`);
    }
  }
  return 0;
}

module.exports = { learnCommand, learnInteractiveCommand };

'use strict';

const fs = require('fs');
const path = require('path');
const { readProjectConfig } = require(path.join(__dirname, '..', '..', 'utils', 'project-config'));
const { categorizeViolations } = require('../analyze/categorizer');
const { createPhases } = require('./phaser');
const { writeRoadmap } = require('./roadmap');

function numArg(v, d) { const n = Number(v); return Number.isFinite(n) && n > 0 ? n : d; }

function planCommand(cwd, args) {
  try {
    const input = args._ && args._[1] ? args._[1] : (args.input || 'lint-results.json');
    const outFile = args.output || 'FIXES-ROADMAP.md';
    const phasesN = args.phases ? Number(args.phases) : 4;
    const team = args['team-size'] ? Number(args['team-size']) : 1;

    const file = path.isAbsolute(input) ? input : path.join(cwd, input);
    const raw = fs.readFileSync(file, 'utf8');
    const json = JSON.parse(raw);

    const cfg = readProjectConfig({ getFilename: () => path.join(cwd, 'placeholder.js'), getCwd: () => cwd });
    const categories = categorizeViolations(json, cfg);
    const phases = createPhases(categories, { phases: phasesN, teamSize: team, cfg });
    const topFiles = numArg(args['top-files'] || args.topFiles, 10);
    const minCount = numArg(args['min-count'] || args.minCount, 1);
    writeRoadmap(path.join(cwd, outFile), { phases, categories, cfg, topFiles, minCount });
    return 0;
  } catch (e) {
    console.error(`plan failed: ${e && e.message}`);
    return 1;
  }
}

