'use strict';

const fs = require('fs');

function countByRule(list, min = 1) {
  const m = new Map();
  for (const r of list || []) {
    const k = String(r.ruleId || 'unknown');
    m.set(k, (m.get(k) || 0) + 1);
  }
  return Array.from(m.entries()).filter(([,c]) => c >= min).sort((a,b)=>b[1]-a[1]);
}

function topFiles(list, limit = 10, min = 1) {
  const m = new Map();
  for (const r of list || []) {
    const k = String(r.filePath || 'unknown');
    m.set(k, (m.get(k) || 0) + 1);
  }
  return Array.from(m.entries()).filter(([,c]) => c >= min).sort((a,b)=>b[1]-a[1]).slice(0, limit);
}

function writeRoadmap(outPath, { phases, categories, topFiles: topFilesN = 10, minCount = 1 } = {}) {
  const lines = [];
  lines.push('# FIXES Roadmap');
  lines.push('');
  // Quick summary
  lines.push('## Summary');
  lines.push(`- Total complexity: ${categories.complexity.length}`);
  lines.push(`- Total architecture: ${categories.architecture.length}`);
  lines.push(`- Total domain terms: ${categories.domainTerms.length}`);
  lines.push(`- Total magic numbers: ${categories.magicNumbers.length}`);
  lines.push('');

  phases.forEach((ph, idx) => {
    lines.push(`## Phase ${idx + 1}: ${ph.name}`);
    lines.push(`Items: ${ph.items.length}`);
    lines.push('');
    if (ph.name === 'Quick Wins') {
      const fixable = categories.counts?.autoFixable || 0;
      lines.push('### Tasks');
      lines.push(`- [ ] Run auto-fix: npx eslint --fix .  (est ~5-10m)   fixes ~${fixable} issues`);
      lines.push('- [ ] Re-run analysis to update counts');
      lines.push('');
      lines.push('### Success Metrics');
      lines.push(`- Auto-fix reduced violations by ~${fixable}`);
      lines.push('');
    }
    if (ph.name === 'Domain Cleanup') {
      lines.push('### Tasks');
      const termRules = countByRule(categories.domainTerms, minCount).slice(0, 5);
      termRules.forEach(([rule, count]) => lines.push(`- [ ] Tidy ${rule} (top ${count})`));
      const files = topFiles(categories.domainTerms, Math.min(5, topFilesN), minCount);
      if (files.length) {
        lines.push('### Top Files');
        files.forEach(([f, count]) => lines.push(`- ${f}: ${count}`));
      }
      lines.push('');
      lines.push('### Success Metrics');
      lines.push('- Domain-term rule counts reduced by 80%');
      lines.push('');
    }
    if (ph.name === 'Refactoring') {
      lines.push('### Tasks');
      const rules = countByRule(categories.complexity, minCount).slice(0, 5);
      rules.forEach(([rule, count]) => lines.push(`- [ ] Reduce ${rule} (top ${count})`));
      const files = topFiles(categories.complexity, Math.min(5, topFilesN), minCount);
      if (files.length) {
        lines.push('### Top Files');
        files.forEach(([f, count]) => lines.push(`- ${f}: ${count}`));
      }
      lines.push('');
      lines.push('### Success Metrics');
      lines.push('- Complexity rule counts below thresholds');
      lines.push('');
    }
    if (ph.name === 'Polish') {
      lines.push('### Tasks');
      const rules = countByRule(categories.architecture, minCount).slice(0, 5);
      rules.forEach(([rule, count]) => lines.push(`- [ ] Tidy ${rule} (top ${count})`));
      const files = topFiles(categories.architecture, Math.min(5, topFilesN), minCount);
      if (files.length) {
        lines.push('### Top Files');
        files.forEach(([f, count]) => lines.push(`- ${f}: ${count}`));
      }
      lines.push('');
      lines.push('### Success Metrics');
      lines.push('- Architecture rule counts within configured limits');
      lines.push('');
    }
  });
  fs.writeFileSync(outPath, lines.join('\n') + '\n');
}

module.exports = { writeRoadmap };

'use strict';

const path = require('path');

async function setupCommand(cwd, args) {
  const { learnCommand } = require(path.join(__dirname, '..', 'learn'));
  const { initCommand } = require(path.join(__dirname, '..', 'init'));
  const { detectProjectContext } = require(path.join(__dirname, '..', '..', 'utils', 'project-detection'));

  // 1) Learn phase (unless skipped)
  const skipLearn = !!(args['skip-learn'] || args.skipLearn);
  if (!skipLearn) {
    const learnArgs = { ...args };
    // If --yes provided, do a non-interactive strict apply
    if (args.yes) {
      learnArgs.strict = true;
      learnArgs.apply = true;
      delete learnArgs.interactive;
    } else {
      // Prefer interactive reconciliation when TTY
      learnArgs.interactive = true;
    }
    const code = await Promise.resolve(learnCommand(cwd, learnArgs));
    if (code !== 0) return code;
  }

  // 2) Init phase: generate everything by default (AGENTS.md + ESLint); cursor remains opt-in
  const initArgs = { ...args, yes: true };

  // Phase 2: Domain handling for non-interactive setup
  if (initArgs.yes && !initArgs.primary) {
    const ctx = detectProjectContext(cwd);
    const inferred = ctx && ctx.type === 'dev-tools' ? 'dev-tools'
      : ctx && ctx.type === 'cli' ? 'cli'
      : ctx && ctx.type === 'web-app' ? 'web-app'
      : null;
    if (inferred) {
      initArgs.primary = inferred;
    } else {
      console.error('Error: --primary required when using --yes (unable to infer domain)');
      console.error('Example: npx eslint-plugin-ai-code-snifftest setup --yes --primary=dev-tools');
      return 1;
    }
  }

  // Ensure changes from init persist to .ai-coding-guide.json after learn
  const prevForce = process.env.FORCE_AI_CONFIG;
  process.env.FORCE_AI_CONFIG = '1';
  const code2 = initCommand(cwd, initArgs);
  if (prevForce === undefined) delete process.env.FORCE_AI_CONFIG; else process.env.FORCE_AI_CONFIG = prevForce;
  return code2;
}

module.exports = { setupCommand };

