    function shouldSkipScientificCalculation(node) {
      const src = context.getSourceCode().getText(node);

      // Multi-day formula: N * 86400e3 (2..365 days)
      const md = src.match(/^\s*(?:([2-9]|[1-9]\d|[12]\d\d|3[0-5]\d))\s*\*\s*86400e3\s*$/) ||
                 src.match(/^\s*86400e3\s*\*\s*([2-9]|[1-9]\d|[12]\d\d|3[0-5]\d)\s*$/);
      if (md) return true;

      // Contains domain constants (restricted set)
      if (PHYSICAL_CONSTANT_SNIPPETS.some((c) => src.includes(c))) return true;


      // Special-case 360: only when division AND angle-like variable name context
      if ((/\/\s*360|360\s*\//.test(src))) {
        const owner = getOwningIdentifierName(node) || '';
        if (owner && /angle|rotation|degree|azimuth|longitude|latitude/i.test(owner)) {
          return true;
        }
      }

      // Music-specific: 440 or 44100 only with strong context
      if (/(^|\W)(440|44100)(\W|$)/.test(src)) {
        const owner = getOwningIdentifierName(node) || '';
        const filename = context.getFilename ? String(context.getFilename()) : '';
        const strongMusicContext = /^(frequency|pitch|tuning|concert|a4)$/i.test(owner) ||
                                   /music|audio/i.test(owner) || /\/music\//i.test(filename) || /\/audio\//i.test(filename);
        if (strongMusicContext) return true;
      }

      // Scientific variable names (general context)
      const owner = getOwningIdentifierName(node);
      if (owner) {
        const lower = owner.toLowerCase();
        if (SCIENTIFIC_TERMS.some((t) => lower.includes(t))) return true;
        // NEW: Use constants library semantic term detection
        if (hasScientificTerm(owner)) return true;
      }

      return false;
    }

    // Read project config once per lint run
    const projectConfig = readProjectConfig(context);

    // Build effective constants map (config + optional external merged)
// Cache merged constants across files for this CWD
    const extCache = (global.__aiSnifftestExtcache = global.__aiSnifftestExtcache || {});
    const cwd = (typeof context.getCwd === 'function' && context.getCwd()) || process.cwd();

    function normalizeNumber(n) {
      if (typeof n === 'number') return Number.isFinite(n) ? n : null;
      const parsed = Number(n);
      return Number.isFinite(parsed) ? parsed : null;
    }

    function buildEffectiveConstantsMap() {
      const baseMap = {};
      // from config
      if (projectConfig && projectConfig.constants) {
        for (const [domain, domainConstants] of Object.entries(projectConfig.constants)) {
          if (!Array.isArray(domainConstants)) continue;
          const set = new Set();
          for (const constantEntry of domainConstants) {
            const v = normalizeNumber(constantEntry);
            if (v !== null) set.add(v);
          }
          if (set.size) baseMap[domain] = Array.from(set);
        }
      }
      if (!projectConfig.experimentalExternalConstants) return baseMap;
      // external discovery (cache per cwd)
      try {
        const key = `merged:${cwd}`;
        let merged = extCache[key];
        if (!merged) {
          const { discoverConstants } = require(path.join(__dirname, '..', 'utils', 'discover-constants'));
          const { mergeConstants } = require(path.join(__dirname, '..', 'utils', 'merge-constants'));
          const discovered = discoverConstants(cwd);
          merged = mergeConstants(discovered);
          extCache[key] = merged;
        }
        for (const [domain, data] of Object.entries(merged || {})) {
          const set = new Set(baseMap[domain] || []);
          const list = Array.isArray(data.constants) ? data.constants : [];
          for (const c of list) {
            const v = normalizeNumber(typeof c === 'object' ? c.value : c);
            if (v !== null) set.add(v);
          }
          if (set.size) baseMap[domain] = Array.from(set);
        }
      } catch {
        // best-effort; ignore discovery errors in rule
      }
      return baseMap;
    }

    const EFFECTIVE_CONSTANTS = buildEffectiveConstantsMap();

    function matchDomainsForValue(value) {
      const domains = [];
      const eps = 1e-12;
      for (const domain of Object.keys(EFFECTIVE_CONSTANTS || {})) {
        const domainValues = EFFECTIVE_CONSTANTS[domain];
        if (!Array.isArray(domainValues)) continue;
        for (const entry of domainValues) {
          let n = null;
          if (typeof entry === 'number') n = entry;
          else if (typeof entry === 'string') {
            const parsed = Number(entry);
            if (!Number.isNaN(parsed)) n = parsed; else continue;
          }
          if (n !== null) {
            if (Object.is(n, value) || Math.abs(n - value) <= eps) { domains.push(domain); break; }
          }
        }
      }
      return domains;
    }

    // preserved for potential future use
    // function isDeclaredConstant(value) {
    //   return matchDomainsForValue(value).length > 0;
    // }

    function shouldSkipByDomainResolution(value, node) {
      // 1) Inline annotation
      const ann = getDomainAnnotation(node, context);
      if (ann) {
        return matchDomainsForValue(value).includes(ann);
      }
      // 2) Variable name driven
      const owner = getOwningIdentifierName(node);
      if (owner) {
        const dn = getDomainForName(owner);
        if (dn && matchDomainsForValue(value).includes(dn)) return true;
      }
      // 3) File-level domains
      const fileDomains = getFileDomains(context);
      if (fileDomains && fileDomains.length) {
        const matches = matchDomainsForValue(value);
        if (matches.some((d) => fileDomains.includes(d))) return true;
      }
      // 4) Explicit constantResolution
      const map = projectConfig.constantResolution || {};
      const key = String(value);
      if (map[key]) return true;
      // 5) Primary/domainPriority
      const pri = Array.isArray(projectConfig.domainPriority) ? projectConfig.domainPriority : [];
      if (pri.length) {
        const matches = matchDomainsForValue(value);
        if (matches.length && pri.includes(matches[0])) return true;
      }
      return false;
    }

    return {
      BinaryExpression(node) {
        // Skip if not all operands are literals
        if (!isAllLiterals(node)) {
          return;
        }

        // Skip if parent is also a BinaryExpression with all literals
        // (we want to report the outermost expression only)
        if (node.parent && node.parent.type === 'BinaryExpression' && isAllLiterals(node.parent)) {
          return;
        }

        // Skip if in special contexts
        if (shouldSkipContext(node)) {
          return;
        }

        // Only handle numeric calculations
        const numericResult = evaluateTree(node);

        if (numericResult === null || typeof numericResult !== 'number') {
          return;
        }

        // Skip when declared by domain resolution (annotations/name/file/explicit/priority)
        if (shouldSkipByDomainResolution(numericResult, node)) {
          return;
        }

        // NEW: skip likely scientific formulas
        if (shouldSkipScientificCalculation(node)) {
          return;
        }

        // Ambiguity warning: if value maps to multiple domains and not otherwise resolved
        const matches = matchDomainsForValue(numericResult);
        if (matches.length > 1 && !shouldSkipByDomainResolution(numericResult, node)) {
          context.report({ node, messageId: 'ambiguousConstant', data: { value: String(numericResult), domains: matches.join(', ') } });
          return;
        }

        // Report the issue
        const src = context.getSourceCode().getText(node);
        const formatted = formatNumber(numericResult, src);
        const options = (context.options && context.options[0]) || {};
        const addSuggestion = !!options.preserveIntentSuggestion;
        const report = {
          node,
          messageId: 'redundantCalculation',
          data: { result: formatted },
          fix(fixer) { return fixer.replaceText(node, formatted); }
        };
        if (addSuggestion) {
          report.suggest = [
            {
              messageId: 'preserveIntent',
              fix(fixer) {
                return fixer.replaceText(node, `${src} /* = ${formatted} */`);
              }
            }
          ];
        }
        context.report(report);
      }
    };
  },
};

/**
 * @fileoverview Simplify redundant conditional expressions
 * @author mojoatomic
 */
'use strict';

//------------------------------------------------------------------------------
// Rule Definition
//------------------------------------------------------------------------------

/** @type {import('eslint').Rule.RuleModule} */
module.exports = {
  meta: {
    type: 'suggestion',
    docs: {
      description: 'Simplify redundant conditional expressions',
      recommended: true,
      url: 'https://github.com/mojoatomic/eslint-plugin-ai-code-snifftest/blob/main/docs/rules/no-redundant-conditionals.md',
    },
    fixable: 'code',
    schema: [],
    messages: {
      constantCondition: 'Condition is always {{value}}. {{fix}}',
      redundantBoolean: 'Redundant boolean comparison: {{original}}',
      redundantTernary: 'Redundant ternary expression can be simplified',
      redundantLogical: 'Redundant logical expression: {{original}}',
      redundantSwitch: 'Switch with constant discriminant can be simplified',
    },
  },

  create(context) {
    const sourceCode = context.sourceCode;

    //----------------------------------------------------------------------
    // Helpers
    //----------------------------------------------------------------------

    /**
     * Check if a node is a constant literal value
     */
    function isConstant(node) {
      if (node.type === 'Literal') {
        return true;
      }

      if (node.type === 'Identifier' && (node.name === 'undefined' || node.name === 'NaN')) {
        return true;
      }

      // Unary expressions with constant operands (-1, +5, etc)
      if (node.type === 'UnaryExpression' && isConstant(node.argument)) {
        return true;
      }

      // Binary expressions with constant operands
      if (node.type === 'BinaryExpression' && isConstant(node.left) && isConstant(node.right)) {
        // Only treat as constant if we can evaluate safely
        const val = evaluateConstant(node);
        return val !== null;
      }

      // Object and array literals are always truthy
      if (node.type === 'ObjectExpression' || node.type === 'ArrayExpression') {
        return true;
      }

      return false;
    }

    /**
     * Get the boolean value of a constant
     */
    function getBooleanValue(node) {
      if (node.type === 'Literal') {
        return Boolean(node.value);
      }

      if (node.type === 'Identifier') {
        if (node.name === 'undefined' || node.name === 'NaN') return false;
      }

      // Unary expressions: evaluate recursively
      if (node.type === 'UnaryExpression') {
        const argValue = getBooleanValue(node.argument);
        if (argValue !== null) {
          if (node.operator === '-') {
            // -0 is falsy, any other negative number is truthy
            const literalValue = node.argument.type === 'Literal' ? node.argument.value : null;
            return literalValue === 0 ? false : argValue;
          }
          if (node.operator === '+') {
            return argValue;
          }
          if (node.operator === '!') {
            return !argValue;
          }
        }
        return null;
      }

      // Binary expressions with constant operands
      if (node.type === 'BinaryExpression') {
        const val = evaluateConstant(node);
        if (val === null) return null;
        return Boolean(val);
      }

      // Object and array literals are always truthy
      if (node.type === 'ObjectExpression' || node.type === 'ArrayExpression') {
        return true;
      }

      return null;
    }

    /**
     * Check if expression compares value === true or value !== false
     */
    function isRedundantBooleanComparison(node) {
      if (node.type !== 'BinaryExpression') return null;

      const { operator, left, right } = node;
      const isBoolLiteral = (n, val) => n.type === 'Literal' && typeof n.value === 'boolean' && n.value === val;

      // Only consider comparisons with boolean literals
      // x === true, x == true  x
      if ((operator === '===' || operator === '==') && isBoolLiteral(right, true)) {
        return { node: left, negated: false, strict: operator === '===' };
      }
      if ((operator === '===' || operator === '==') && isBoolLiteral(left, true)) {
        return { node: right, negated: false, strict: operator === '===' };
      }

      // x !== false, x != false  x
      if ((operator === '!==' || operator === '!=') && isBoolLiteral(right, false)) {
        return { node: left, negated: false, strict: operator === '!==' };
      }
      if ((operator === '!==' || operator === '!=') && isBoolLiteral(left, false)) {
        return { node: right, negated: false, strict: operator === '!==' };
      }

      // x === false, x == false  !x
      if ((operator === '===' || operator === '==') && isBoolLiteral(right, false)) {
        return { node: left, negated: true, strict: operator === '===' };
      }
      if ((operator === '===' || operator === '==') && isBoolLiteral(left, false)) {
        return { node: right, negated: true, strict: operator === '===' };
      }

      // x !== true, x != true  !x
      if ((operator === '!==' || operator === '!=') && isBoolLiteral(right, true)) {
        return { node: left, negated: true, strict: operator === '!==' };
      }
      if ((operator === '!==' || operator === '!=') && isBoolLiteral(left, true)) {
        return { node: right, negated: true, strict: operator === '!==' };
      }

      return null;
    }

    //----------------------------------------------------------------------
    // Public
    //----------------------------------------------------------------------

    // Simplify logical expressions in conditions when safe
    // Evaluate constant value of an expression, or null if unknown
    function evaluateConstant(node) {
      if (node.type === 'Literal') return node.value;
      if (node.type === 'Identifier') {
        if (node.name === 'undefined') return undefined;
        if (node.name === 'NaN') return NaN;
        return null;
      }
      if (node.type === 'UnaryExpression') {
        const v = evaluateConstant(node.argument);
        if (v === null) return null;
        switch (node.operator) {
          case '-': return typeof v === 'number' ? -v : null;
          case '+': return typeof v === 'number' ? +v : null;
          case '!': return !v;
          default: return null;
        }
      }
      if (node.type === 'BinaryExpression') {
        const l = evaluateConstant(node.left);
        const r = evaluateConstant(node.right);
        if (l === null || r === null) return null;
        switch (node.operator) {
          case '+':
            if (typeof l === 'number' && typeof r === 'number') return l + r;
            if (typeof l === 'string' || typeof r === 'string') return String(l) + String(r);
            return null;
          case '-': case '*': case '/': case '%': case '**':
            if (typeof l === 'number' && typeof r === 'number') return evalNumberOp(node.operator, l, r);
            return null;
          case '<': case '>': case '<=': case '>=':
            if (typeof l === 'number' && typeof r === 'number') return evalRelOp(node.operator, l, r);
            return null;
          case '===': return l === r;
          case '!==': return l !== r;
          default: return null;
        }
      }
      return null;
    }

    function evalNumberOp(op, l, r) {
      switch (op) {
        case '-': return l - r;
        case '*': return l * r;
        case '/': return l / r;
        case '%': return l % r;
        case '**': return l ** r;
        default: return NaN;
      }
    }

    function evalRelOp(op, l, r) {
      switch (op) {
        case '<': return l < r;
        case '>': return l > r;
        case '<=': return l <= r;
        case '>=': return l >= r;
        default: return false;
      }
    }

    function simplifyLogicalExpression(test, node) {
      if (test.type !== 'LogicalExpression') return null;
      const rawLeftVal = getBooleanValue(test.left);
      const rawRightVal = getBooleanValue(test.right);
      // Treat non-special identifiers as unknown (null), regardless of heuristic boolean value
      const leftVal = (test.left.type === 'Identifier' && test.left.name !== 'undefined' && test.left.name !== 'NaN') ? null : rawLeftVal;
      const rightVal = (test.right.type === 'Identifier' && test.right.name !== 'undefined' && test.right.name !== 'NaN') ? null : rawRightVal;
      const op = test.operator;
      const leftText = sourceCode.getText(test.left);
      const rightText = sourceCode.getText(test.right);

      // Helper to build constant-condition style fix
      function constantFix(value) {
        return function(fixer) {
          if (value) {
            if (node.consequent.type === 'BlockStatement') {
              const bodyText = sourceCode.getText(node.consequent).slice(1, -1).trim();
              return fixer.replaceText(node, bodyText);
            }
            return fixer.replaceText(node, sourceCode.getText(node.consequent));
          }
          if (node.alternate) {
            if (node.alternate.type === 'BlockStatement') {
              const altText = sourceCode.getText(node.alternate).slice(1, -1).trim();
              return fixer.replaceText(node, altText);
            }
            return fixer.replaceText(node, sourceCode.getText(node.alternate));
          }
          return fixer.remove(node);
        };
      }

      // Evaluate simplifications
      if (op === '&&') {
        // Constant false if either side is known false
        if (leftVal === false || rightVal === false) {
          return { kind: 'constant', value: false, fix: constantFix(false) };
        }
        // Simplify when one side is known true and the other is unknown
        if (leftVal === true && rightVal === null) {
          return { kind: 'expr', text: rightText };
        }
        if (rightVal === true && leftVal === null) {
          return { kind: 'expr', text: leftText };
        }
      }
      if (op === '||') {
        // Constant true if either side is known true
        if (leftVal === true || rightVal === true) {
          return { kind: 'constant', value: true, fix: constantFix(true) };
        }
        // Simplify when one side is known false and the other is unknown
        if (leftVal === false && rightVal === null) {
          return { kind: 'expr', text: rightText };
        }
        if (rightVal === false && leftVal === null) {
          return { kind: 'expr', text: leftText };
        }
      }
      return null;
    }

    return {
      IfStatement(node) {
        const { test } = node;

        // Check for constant condition
        if (isConstant(test)) {
          const boolValue = getBooleanValue(test);
          const boolValueStr = String(boolValue);

          context.report({
            node: test,
            messageId: 'constantCondition',
            data: {
              value: boolValueStr,
              fix: boolValue ? 'Remove the conditional' : 'This code is unreachable'
            },
            fix(fixer) {
              if (boolValue) {
                // if (true) { A } else { B }  A (drop else)
                if (node.consequent.type === 'BlockStatement') {
                  const bodyText = sourceCode.getText(node.consequent).slice(1, -1).trim();
                  return fixer.replaceText(node, bodyText);
                }
                return fixer.replaceText(node, sourceCode.getText(node.consequent));
              }
              // value === false
              // if (false) { A } else { B }  B; if no else, remove
              if (node.alternate) {
                if (node.alternate.type === 'BlockStatement') {
                  const altText = sourceCode.getText(node.alternate).slice(1, -1).trim();
                  return fixer.replaceText(node, altText);
                }
                return fixer.replaceText(node, sourceCode.getText(node.alternate));
              }
              return fixer.remove(node);
            },
          });
          return; // avoid double-reporting on the same test
        }

        // Check for redundant boolean comparison
        const boolCheck = isRedundantBooleanComparison(test);
        if (boolCheck) {
          const original = sourceCode.getText(test);
          const simplified = boolCheck.negated 
            ? `!${sourceCode.getText(boolCheck.node)}`
            : sourceCode.getText(boolCheck.node);

          context.report({
            node: test,
            messageId: 'redundantBoolean',
            data: { original },
            fix(fixer) {
              return fixer.replaceText(test, simplified);
            },
          });
        }

        // Logical expression simplification (short-circuit)
        if (test.type === 'LogicalExpression') {
          const simp = simplifyLogicalExpression(test, node);
          if (simp) {
            if (simp.kind === 'expr') {
              const original = sourceCode.getText(test);
              context.report({
                node: test,
                messageId: 'redundantLogical',
                data: { original },
                fix(fixer) {
                  return fixer.replaceText(test, simp.text);
                }
              });
            } else if (simp.kind === 'constant') {
              // Report as constant condition and fix whole statement safely
              context.report({
                node: test,
                messageId: 'constantCondition',
                data: { value: String(simp.value), fix: simp.value ? 'Remove the conditional' : 'This code is unreachable' },
                fix: simp.fix,
              });
            }
          }
        }
      },

      // Loop statements
      WhileStatement(node) {
        const { test } = node;
        if (isConstant(test)) {
          const boolValue = getBooleanValue(test);
          const boolValueStr = String(boolValue);
          context.report({
            node: test,
            messageId: 'constantCondition',
            data: {
              value: boolValueStr,
              fix: boolValue ? 'Potential infinite loop' : 'This loop is unreachable'
            },
            fix(fixer) {
              if (!boolValue) {
                return fixer.remove(node);
              }
              // Do not auto-fix while (true)
              return null;
            }
          });
        }
      },

      DoWhileStatement(node) {
        const { test } = node;
        if (isConstant(test)) {
          const boolValue = getBooleanValue(test);
          const boolValueStr = String(boolValue);
          context.report({
            node: test,
            messageId: 'constantCondition',
            data: {
              value: boolValueStr,
              fix: boolValue ? 'Potential infinite loop' : 'Simplify: body runs once'
            },
            fix(fixer) {
              if (!boolValue) {
                // do { ... } while (false);  body
                if (node.body && node.body.type === 'BlockStatement') {
                  const bodyText = sourceCode.getText(node.body).slice(1, -1).trim();
                  return fixer.replaceText(node, bodyText);
                }
                // Non-block body: skip auto-fix for safety
                return null;
              }
              return null;
            }
          });
        }
      },

      ForStatement(node) {
        const { test } = node;
        // for (;;) has null test  treated as true
        if (test === null || isConstant(test)) {
          const boolValue = test === null ? true : getBooleanValue(test);
          const boolValueStr = String(boolValue);
          context.report({
            node: test || node,
            messageId: 'constantCondition',
            data: {
              value: boolValueStr,
              fix: boolValue ? 'Potential infinite loop' : 'This loop is unreachable'
            },
            fix(fixer) {
              if (!boolValue) {
                return fixer.remove(node);
              }
              return null;
            }
          });
        }
      },

      SwitchStatement(node) {
        // Only proceed if discriminant is constant
        const discVal = evaluateConstant(node.discriminant);
        if (discVal === null) return;

        // Find matching case (using strict equality semantics)
        let matchingIndex = -1;
        for (let i = 0; i < node.cases.length; i++) {
          const c = node.cases[i];
          if (c.test === null) {
            continue; // default, handle later if no match
          }
          const testVal = evaluateConstant(c.test);
          if (testVal !== null && testVal === discVal) {
            matchingIndex = i;
            break;
          }
        }

        const hasDefault = node.cases.some(c => c.test === null);
        const defaultIndex = node.cases.findIndex(c => c.test === null);

        function isSafeCase(caseNode, isLastCase) {
          const cons = caseNode.consequent || [];
          if (cons.length === 0) return false; // empty  fallthrough
          const lastStmt = cons[cons.length - 1];
          // safe if ends in break/return/throw/continue OR it's last case and ends execution
          if (lastStmt.type === 'BreakStatement' || lastStmt.type === 'ReturnStatement' || lastStmt.type === 'ThrowStatement' || lastStmt.type === 'ContinueStatement') {
            return true;
          }
          // If it's the last case, we allow no break (no fallthrough)
          return isLastCase;
        }

        function buildBodyText(caseNode) {
          const parts = [];
          for (const stmt of caseNode.consequent) {
            if (stmt.type === 'BreakStatement') continue;
            parts.push(sourceCode.getText(stmt));
          }
          return parts.join('\n');
        }

        // Report and possibly fix
        if (matchingIndex >= 0) {
          const caseNode = node.cases[matchingIndex];
          const isLast = matchingIndex === node.cases.length - 1;
          const safe = isSafeCase(caseNode, isLast);
          if (safe) {
            const bodyText = buildBodyText(caseNode);
            context.report({
              node,
              messageId: 'redundantSwitch',
              fix(fixer) {
                return fixer.replaceText(node, bodyText);
              }
            });
          } else {
            context.report({ node, messageId: 'redundantSwitch' });
          }
          return;
        }

        // No matching case
        if (hasDefault) {
          const caseNode = node.cases[defaultIndex];
          const isLast = defaultIndex === node.cases.length - 1;
          const safe = isSafeCase(caseNode, isLast);
          if (safe) {
            const bodyText = buildBodyText(caseNode);
            context.report({
              node,
              messageId: 'redundantSwitch',
              fix(fixer) {
                return fixer.replaceText(node, bodyText);
              }
            });
          } else {
            context.report({ node, messageId: 'redundantSwitch' });
          }
        } else {
          // No matching and no default  no-op switch
          context.report({
            node,
            messageId: 'redundantSwitch',
            fix(fixer) {
              return fixer.remove(node);
            }
          });
        }
      },

      ConditionalExpression(node) {
        const { test, consequent, alternate } = node;

        // Check for x ? true : false (boolean literals only)
        if (
          consequent.type === 'Literal' && typeof consequent.value === 'boolean' && consequent.value &&
          alternate.type === 'Literal' && typeof alternate.value === 'boolean' && !alternate.value
        ) {
          context.report({
            node,
            messageId: 'redundantTernary',
            fix(fixer) {
              return fixer.replaceText(node, `Boolean(${sourceCode.getText(test)})`);
            },
          });
        }

        // Check for x ? false : true (boolean literals only)
        if (
          consequent.type === 'Literal' && typeof consequent.value === 'boolean' && !consequent.value &&
          alternate.type === 'Literal' && typeof alternate.value === 'boolean' && alternate.value
        ) {
          context.report({
            node,
            messageId: 'redundantTernary',
            fix(fixer) {
              return fixer.replaceText(node, `!${sourceCode.getText(test)}`);
            },
          });
        }

        // Check for x ? value : value (identical branches)
        const consequentText = sourceCode.getText(consequent);
        const alternateText = sourceCode.getText(alternate);
        if (consequentText === alternateText) {
          context.report({
            node,
            messageId: 'redundantTernary',
            fix(fixer) {
              return fixer.replaceText(node, consequentText);
            },
          });
        }
      },
    };
  },
};

/**
 * @fileoverview Suggest inlining trivial single-use wrapper functions that add no value
 * @author mojoatomic
 */
'use strict';

//------------------------------------------------------------------------------
// Rule Definition
//------------------------------------------------------------------------------

/** @type {import('eslint').Rule.RuleModule} */
module.exports = {
  meta: {
    type: 'suggestion',
    docs: {
      description: 'Suggest inlining trivial single-use wrapper functions that add no value',
      recommended: false,
      url: 'https://github.com/mojoatomic/eslint-plugin-ai-code-snifftest/blob/main/docs/rules/no-unnecessary-abstraction.md',
    },
    hasSuggestions: true,
    schema: [],
    messages: {
      unnecessaryWrapper: 'Function \'{{name}}\' is a trivial wrapper that only calls \'{{wrappedName}}\'. Consider inlining it.',
      inlineFunction: 'Inline the wrapper function',
    },
  },

  create(context) {
    const sourceCode = context.sourceCode;
    const functionReferences = new Map();
    const functionsUsedAsObjects = new Set();

    //----------------------------------------------------------------------
    // Helpers
    //----------------------------------------------------------------------

    /**
     * Check if a function is a trivial wrapper (only returns a single function call)
     * @param {Object} node - Function node
     * @returns {Object|null} - Wrapped call info or null
     */
    function getTrivialWrapperInfo(node) {
      const body = node.body;

      // Must be a block statement with exactly one return statement
      if (!body || body.type !== 'BlockStatement' || body.body.length !== 1) {
        return null;
      }

      const statement = body.body[0];
      if (statement.type !== 'ReturnStatement' || !statement.argument) {
        return null;
      }

      const returnValue = statement.argument;

      // Must return a call expression
      if (returnValue.type !== 'CallExpression') {
        return null;
      }

      // Get the wrapped function name (only support simple identifiers)
      if (returnValue.callee.type !== 'Identifier') {
        return null;
      }

      const wrappedName = returnValue.callee.name;

      // Check if arguments are simply passed through
      const wrapperParams = node.params;
      const callArgs = returnValue.arguments;

      // Must have same number of arguments
      if (wrapperParams.length !== callArgs.length) {
        return null;
      }

      // Each argument must match the corresponding parameter
      for (let i = 0; i < wrapperParams.length; i++) {
        const param = wrapperParams[i];
        const arg = callArgs[i];

        // Only support simple identifier parameters
        if (param.type !== 'Identifier') {
          return null;
        }

        // Argument must be an identifier with the same name
        if (arg.type !== 'Identifier' || arg.name !== param.name) {
          return null;
        }
      }

      return {
        wrappedName,
        callNode: returnValue,
      };
    }

    /**
     * Track function references
     */
    function trackFunction(node) {
      let name = null;

      if (node.type === 'FunctionDeclaration' && node.id) {
        name = node.id.name;
      } else if (node.parent.type === 'VariableDeclarator' && node.parent.id.type === 'Identifier') {
        name = node.parent.id.name;
      }

      if (name) {
        if (!functionReferences.has(name)) {
          functionReferences.set(name, { node, callCount: 0 });
        }
      }
    }

    /**
     * Count function call references
     */
    function countReference(node) {
      if (node.callee.type === 'Identifier') {
        const name = node.callee.name;
        const ref = functionReferences.get(name);
        if (ref) {
          ref.callCount++;
        }
      }
    }

    /**
     * Track when functions are used as objects (property access, not just calls)
     */
    function trackMemberExpression(node) {
      if (node.object.type === 'Identifier') {
        functionsUsedAsObjects.add(node.object.name);
      }
    }

    /**
     * Track when functions are passed as values (not called)
     */
    function trackIdentifierUsage(node) {
      // Skip if it's a function declaration
      if (node.parent.type === 'FunctionDeclaration') {
        return;
      }

      // Skip if it's a call expression callee
      if (node.parent.type === 'CallExpression' && node.parent.callee === node) {
        return;
      }

      // Skip if it's a variable declarator id
      if (node.parent.type === 'VariableDeclarator' && node.parent.id === node) {
        return;
      }

      // Skip if it's a function parameter
      if (node.parent.type === 'FunctionDeclaration' ||
          node.parent.type === 'FunctionExpression' ||
          node.parent.type === 'ArrowFunctionExpression') {
        return;
      }

      // Track functions used as values (callbacks, property assignments, etc)
      if (functionReferences.has(node.name)) {
        functionsUsedAsObjects.add(node.name);
      }
    }

    //----------------------------------------------------------------------
    // Public
    //----------------------------------------------------------------------

    return {
      FunctionDeclaration: trackFunction,
      FunctionExpression: trackFunction,
      ArrowFunctionExpression: trackFunction,
      CallExpression: countReference,
      MemberExpression: trackMemberExpression,
      Identifier: trackIdentifierUsage,

      'Program:exit'() {
        // Check each function to see if it's a trivial wrapper used only once
        for (const [name, { node, callCount }] of functionReferences) {
          // Only report if called exactly once
          if (callCount !== 1) {
            continue;
          }

          const wrapperInfo = getTrivialWrapperInfo(node);
          if (!wrapperInfo) {
            continue;
          }

          // Skip exported functions (they are public API)
          if (node.parent.type === 'ExportNamedDeclaration' || 
              node.parent.type === 'ExportDefaultDeclaration') {
            continue;
          }

          // Skip functions exported via variable declaration
          if (node.parent.type === 'VariableDeclarator' &&
              node.parent.parent.parent &&
              node.parent.parent.parent.type === 'ExportNamedDeclaration') {
            continue;
          }

          // Skip functions used as objects (property access, passed as callbacks, etc)
          if (functionsUsedAsObjects.has(name)) {
            continue;
          }

          // For function expressions/arrows in variable declarations, remove the entire declaration
          let functionNode;
          if (node.parent.type === 'VariableDeclarator') {
            // node.parent = VariableDeclarator
            // node.parent.parent = VariableDeclaration
            functionNode = node.parent.parent;
          } else {
            // FunctionDeclaration
            functionNode = node;
          }

          context.report({
            node: functionNode,
            messageId: 'unnecessaryWrapper',
            data: {
              name,
              wrappedName: wrapperInfo.wrappedName,
            },
            suggest: [
              {
                messageId: 'inlineFunction',
                fix(fixer) {
                  //Get range including surrounding whitespace
                  let startIndex = functionNode.range[0];
                  let endIndex = functionNode.range[1];
                  const code = sourceCode.text;

                  // Include leading whitespace from beginning of line
                  while (startIndex > 0 && code[startIndex - 1] !== '\n') {
                    if (!/\s/.test(code[startIndex - 1])) {
                      break;
                    }
                    startIndex--;
                  }

                  // Include trailing whitespace and newline
                  while (endIndex < code.length && /\s/.test(code[endIndex])) {
                    if (code[endIndex] === '\n') {
                      endIndex++;
                      break;
                    }
                    endIndex++;
                  }

                  return fixer.removeRange([startIndex, endIndex]);
                },
              },
            ],
          });
        }
      },
    };
  },
};

/**
 * @fileoverview Simplify boolean expressions and remove redundant logic
 * @author mojoatomic
 */
'use strict';

//------------------------------------------------------------------------------
// Rule Definition
//------------------------------------------------------------------------------

//------------------------------------------------------------------------------
// Helpers
//------------------------------------------------------------------------------

/**
 * Checks if two nodes are equivalent
 */
function areNodesEquivalent(node1, node2, sourceCode) {
  if (!node1 || !node2) return false;
  return sourceCode.getText(node1) === sourceCode.getText(node2);
}

//------------------------------------------------------------------------------
// Rule Definition
//------------------------------------------------------------------------------

/** @type {import('eslint').Rule.RuleModule} */
module.exports = {
  meta: {
    type: 'suggestion',
    docs: {
      description: 'Simplify boolean expressions and remove redundant logic',
      recommended: true,
      url: null,
    },
    fixable: 'code',
    schema: [],
    messages: {
      unnecessaryComparison: 'Unnecessary comparison with boolean literal. Use {{suggestion}} instead.',
      redundantExpression: 'Redundant expression. Simplify to {{suggestion}}.'
    },
  },

  create(context) {
    const sourceCode = context.sourceCode || context.getSourceCode();

    return {
      BinaryExpression(node) {
        // x === true  x
        // x === false  !x
        if (node.operator === '===' || node.operator === '==') {
          if (node.right.type === 'Literal' && typeof node.right.value === 'boolean') {
            const boolLiteral = node.right.value;
            context.report({
              node,
              messageId: 'unnecessaryComparison',
              data: {
                suggestion: boolLiteral ? sourceCode.getText(node.left) : `!${sourceCode.getText(node.left)}`
              },
              fix(fixer) {
                if (boolLiteral) {
                  return fixer.replaceText(node, sourceCode.getText(node.left));
                } else {
                  return fixer.replaceText(node, `!${sourceCode.getText(node.left)}`);
                }
              }
            });
          }
          if (node.left.type === 'Literal' && typeof node.left.value === 'boolean') {
            const boolLiteral = node.left.value;
            context.report({
              node,
              messageId: 'unnecessaryComparison',
              data: {
                suggestion: boolLiteral ? sourceCode.getText(node.right) : `!${sourceCode.getText(node.right)}`
              },
              fix(fixer) {
                if (boolLiteral) {
                  return fixer.replaceText(node, sourceCode.getText(node.right));
                } else {
                  return fixer.replaceText(node, `!${sourceCode.getText(node.right)}`);
                }
              }
            });
          }
        }

        // x !== false  x
        // x !== true  !x
        if (node.operator === '!==' || node.operator === '!=') {
          if (node.right.type === 'Literal' && typeof node.right.value === 'boolean') {
            const boolLiteral = node.right.value;
            context.report({
              node,
              messageId: 'unnecessaryComparison',
              data: {
                suggestion: boolLiteral ? `!${sourceCode.getText(node.left)}` : sourceCode.getText(node.left)
              },
              fix(fixer) {
                if (boolLiteral) {
                  return fixer.replaceText(node, `!${sourceCode.getText(node.left)}`);
                } else {
                  return fixer.replaceText(node, sourceCode.getText(node.left));
                }
              }
            });
          }
          if (node.left.type === 'Literal' && typeof node.left.value === 'boolean') {
            const boolLiteral = node.left.value;
            context.report({
              node,
              messageId: 'unnecessaryComparison',
              data: {
                suggestion: boolLiteral ? `!${sourceCode.getText(node.right)}` : sourceCode.getText(node.right)
              },
              fix(fixer) {
                if (boolLiteral) {
                  return fixer.replaceText(node, `!${sourceCode.getText(node.right)}`);
                } else {
                  return fixer.replaceText(node, sourceCode.getText(node.right));
                }
              }
            });
          }
        }
      },

      LogicalExpression(node) {
        // x || x  x
        // x && x  x
        if (areNodesEquivalent(node.left, node.right, sourceCode)) {
          context.report({
            node,
            messageId: 'redundantExpression',
            data: {
              suggestion: sourceCode.getText(node.left)
            },
            fix(fixer) {
              return fixer.replaceText(node, sourceCode.getText(node.left));
            }
          });
        }

        // x && y || y  x || y (absorbing element)
        if (node.operator === '||' && 
            node.left.type === 'LogicalExpression' && 
            node.left.operator === '&&') {
          if (areNodesEquivalent(node.left.right, node.right, sourceCode)) {
            context.report({
              node,
              messageId: 'redundantExpression',
              data: {
                suggestion: `${sourceCode.getText(node.left.left)} || ${sourceCode.getText(node.right)}`
              },
              fix(fixer) {
                return fixer.replaceText(node, `${sourceCode.getText(node.left.left)} || ${sourceCode.getText(node.right)}`);
              }
            });
          }
          if (areNodesEquivalent(node.left.left, node.right, sourceCode)) {
            context.report({
              node,
              messageId: 'redundantExpression',
              data: {
                suggestion: `${sourceCode.getText(node.right)} || ${sourceCode.getText(node.left.right)}`
              },
              fix(fixer) {
                return fixer.replaceText(node, `${sourceCode.getText(node.right)} || ${sourceCode.getText(node.left.right)}`);
              }
            });
          }
        }
      }
    };
  },
};

'use strict';

const fs = require('fs');
const path = require('path');

const DEFAULT_IGNORE_DIRS = new Set(['node_modules', '.git', 'dist', 'build', 'coverage', '.next', '.cache']);

function listFiles(root, exts, maxFiles) {
  const out = [];
  function walk(dir) {
    let entries;
    try { entries = fs.readdirSync(dir, { withFileTypes: true }); } catch { /* ignore */ return; }
    for (const ent of entries) {
      if (ent.name.startsWith('.')) {
        // allow .ai-* files but skip hidden dirs by default
        if (ent.isDirectory() && ent.name !== '.ai-constants') continue;
      }
      if (ent.isDirectory()) {
        if (DEFAULT_IGNORE_DIRS.has(ent.name)) continue;
        walk(path.join(dir, ent.name));
        if (maxFiles && out.length >= maxFiles) return;
      } else {
        const fp = path.join(dir, ent.name);
        const ext = path.extname(ent.name).toLowerCase();
        if (!exts.length || exts.includes(ext)) {
          out.push(fp);
        }
        if (maxFiles && out.length >= maxFiles) return;
      }
    }
  }
  walk(root);
  return out;
}

function tokenizeNamesFromSource(src) {
  const names = [];
  try {
    const declRe = /(const|let|var|function)\s+([A-Za-z_$][A-Za-z0-9_$]*)/g;
    let m;
    while ((m = declRe.exec(src))) {
      names.push(m[2]);
    }
    // capture object properties as names too (heuristic)
    const propRe = /([A-Za-z_$][A-Za-z0-9_$]*)\s*:/g;
    while ((m = propRe.exec(src))) {
      names.push(m[1]);
    }
  } catch {
    // ignore parse heuristics errors
  }
  return names;
}

function classifyCasing(name) {
  if (!name) return 'other';

  // Strip leading/trailing _ or $ for classification, but track them
  const stripped = name.replace(/^[_$]+|[_$]+$/g, '');
  if (!stripped) return 'other';  // names like "_" or "$" or "__"

  // Classify the stripped name
  if (/^[A-Z0-9_]+$/.test(stripped) && /_/.test(stripped)) return 'UPPER_SNAKE_CASE';
  if (/^[a-z][A-Za-z0-9]*$/.test(stripped) && /[A-Z]/.test(stripped)) return 'camelCase';
  if (/^[a-z0-9]+(_[a-z0-9]+)+$/.test(stripped)) return 'snake_case';
  if (/^[A-Z][A-Za-z0-9]*$/.test(stripped)) return 'PascalCase';
  if (/^[a-z][a-z0-9]*$/.test(stripped)) return 'camelCase';  // single-word lowercase

  return 'other';
}

function booleanPrefix(name) {
  const prefixes = ['is','has','should','can','did','will','was','were'];
  for (const p of prefixes) {
    if (name && name.startsWith(p) && name.length > p.length && /[A-Z_]/.test(name[p.length])) return p;
  }
  return null;
}

function extractNumerics(src) {
  const out = [];
  const numRe = /(?<![A-Za-z0-9_.])(?:\d+\.\d+|\d+)(?:[eE][+-]?\d+)?/g;
  let m;
  while ((m = numRe.exec(src))) {
    const raw = m[0];
    const v = Number(raw);
    if (!Number.isFinite(v)) continue;
    out.push(v);
  }
  return out;
}

function loadCache(cachePath) {
  try { return JSON.parse(fs.readFileSync(cachePath, 'utf8')); } catch { return { version: 1, files: {} }; }
}

function saveCache(cachePath, cache) {
  try { fs.writeFileSync(cachePath, JSON.stringify(cache, null, 2) + '\n'); } catch { /* ignore cache write */ }
}

function summarizeFileContent(src) {
  const names = tokenizeNamesFromSource(src);
  const casing = { camelCase: 0, 'snake_case': 0, PascalCase: 0, 'UPPER_SNAKE_CASE': 0, other: 0 };
  const boolDist = {};
  let withPrefix = 0;
  let without = 0;
  const genericList = ['data','result','value','info','item','object','obj','arr','array','list','tmp','temp','str','num','bool','flag','state'];
  const genericNames = {};
  for (const n of names) {
    const c = classifyCasing(n);
    casing[c] = (casing[c] || 0) + 1;
    const bp = booleanPrefix(n);
    if (bp) { withPrefix++; boolDist[bp] = (boolDist[bp] || 0) + 1; } else { without++; }
    if (genericList.includes(n)) genericNames[n] = (genericNames[n] || 0) + 1;
  }
  const nums = extractNumerics(src);
  const constCounts = {};
  for (const v of nums) constCounts[v] = (constCounts[v] || 0) + 1;
  const constants = Object.entries(constCounts).map(([value, count]) => ({ value: Number(value), count }));
  return {
    constants,
    naming: {
      casing,
      booleanPrefixes: {
        withPrefix,
        without,
        distribution: boolDist,
        common: Object.keys(boolDist).sort((a,b)=> (boolDist[b]-boolDist[a]))
      }
    },
    genericNames
  };
}

function aggregateSummaries(summaries) {
  const out = { constants: [], naming: { casing: {}, booleanPrefixes: { withPrefix: 0, without: 0, common: [], distribution: {} } }, genericNames: {} };
  const constMap = new Map();
  for (const s of summaries) {
    for (const [k,v] of Object.entries(s.naming.casing || {})) out.naming.casing[k] = (out.naming.casing[k] || 0) + v;
    out.naming.booleanPrefixes.withPrefix += s.naming.booleanPrefixes.withPrefix || 0;
    out.naming.booleanPrefixes.without += s.naming.booleanPrefixes.without || 0;
    for (const [k,v] of Object.entries(s.naming.booleanPrefixes.distribution || {})) out.naming.booleanPrefixes.distribution[k] = (out.naming.booleanPrefixes.distribution[k] || 0) + v;
    for (const [k,v] of Object.entries(s.genericNames || {})) out.genericNames[k] = (out.genericNames[k] || 0) + v;
    for (const c of s.constants || []) constMap.set(c.value, (constMap.get(c.value) || 0) + c.count);
  }
  out.constants = Array.from(constMap.entries()).map(([value, count]) => ({ value, count })).sort((a,b)=>b.count-a.count);
  out.naming.booleanPrefixes.common = Object.keys(out.naming.booleanPrefixes.distribution).sort((a,b)=> (out.naming.booleanPrefixes.distribution[b]-out.naming.booleanPrefixes.distribution[a]));
  // Convert to issue-style findings with confidence
  const totalConst = out.constants.reduce((acc,c)=>acc+c.count,0) || 1;
  out.constants = out.constants.map(c => ({ value: Number(c.value), name: null, confidence: Math.min(1, c.count / Math.max(3, totalConst)) }));
  return out;
}

function scanProject(cwd, options) {
  const opts = Object.assign({ sample: 400, useCache: true, cachePath: path.join(cwd, '.ai-learn-cache.json') }, options || {});
  const files = listFiles(cwd, ['.js','.cjs','.mjs'], opts.sample);
  const cache = opts.useCache ? loadCache(opts.cachePath) : { version: 1, files: {} };
  const summaries = [];
  for (const fp of files) {
    let st; try { st = fs.statSync(fp); } catch { continue; }
    const prev = cache.files[fp];
    if (prev && prev.mtimeMs === st.mtimeMs) {
      summaries.push(prev.summary);
      continue;
    }
    let src; try { src = fs.readFileSync(fp, 'utf8'); } catch { continue; }
    const summary = summarizeFileContent(src);
    summaries.push(summary);
    cache.files[fp] = { mtimeMs: st.mtimeMs, summary };
  }
  if (opts.useCache) saveCache(opts.cachePath, cache);
  return aggregateSummaries(summaries);
}

module.exports = {
  scanProject,
  summarizeFileContent,
  aggregateSummaries
};

'use strict';


const constantsLib = (()=>{ try { return require('../constants'); } catch { return null; }})();

const DEFAULT_SANITY = Object.freeze({
  naming: {
    style: 'camelCase',
    constants: 'UPPER_SNAKE_CASE',
    booleanPrefix: ['is','has','should','can']
  },
  minimumConfidence: 0.7,
  minimumMatch: 0.6
});

function bestNamingStyle(casingCounts) {
  let best = { style: 'camelCase', count: -1 };
  for (const [style, count] of Object.entries(casingCounts || {})) {
    if (count > best.count && style !== 'other') best = { style, count };
  }
  return best.style;
}

function reconcileNaming(found, sane, mode) {
  const casing = (found && found.casing) || {};
  const total = Object.values(casing).reduce((a,b)=>a+b,0) || 1;
  const best = bestNamingStyle(casing);
  const majority = (casing[best] || 0) / total;
  const namingResolution = { style: sane.naming.style, booleanPrefix: sane.naming.booleanPrefix.slice() };
  const warnings = [];
  if (mode === 'strict') {
    namingResolution.style = sane.naming.style;
  } else if (mode === 'permissive') {
    // Prefer current majority regardless of threshold
    namingResolution.style = best || sane.naming.style;
  } else {
    if (majority >= (sane.minimumMatch || 0.6)) {
      namingResolution.style = best;
    } else {
      warnings.push(`Weak naming majority (${Math.round(majority*100)}%)  using sane default '${sane.naming.style}'`);
    }
  }
  // boolean prefixes: keep sane list but ensure the most common in found are included
  const dist = (found.booleanPrefixes && found.booleanPrefixes.distribution) || {};
  const common = Object.keys(dist).sort((a,b)=> (dist[b]-dist[a])).slice(0,4);
  for (const p of common) if (!namingResolution.booleanPrefix.includes(p)) namingResolution.booleanPrefix.push(p);
  if (mode === 'strict') {
    // reset to sane-only list in strict
    namingResolution.booleanPrefix = sane.naming.booleanPrefix.slice();
  }
  return { result: namingResolution, warnings };
}

function findConstantMeta(value) {
  if (!constantsLib || !constantsLib.DOMAINS) return null;
  for (const [domain, mod] of Object.entries(constantsLib.DOMAINS)) {
    const constantMetaList = Array.isArray(mod.constantMeta) ? mod.constantMeta : [];
    for (const m of constantMetaList) {
      if (typeof m.value === 'number' && Math.abs(m.value - value) <= 1e-9) {
        return { domain, name: m.name, description: m.description };
      }
    }
  }
  // fallback: try bare constants list to get domain only
  const dom = constantsLib.getDomainForValue ? constantsLib.getDomainForValue(value) : null;
  if (dom) return { domain: dom, name: null, description: null };
  return null;
}

function reconcileConstants(found, sane, options) {
  const out = [];
  const warnings = [];
  const domainHits = [];
  const minConf = sane.minimumConfidence || 0.7;
  const cfg = (options && options.config) || { domainPriority: [], constantResolution: {} };
  for (const c of (found || [])) {
    if (c.confidence < minConf) continue;
    const forceDomain = cfg.constantResolution && cfg.constantResolution[String(c.value)];
    const meta = findConstantMeta(c.value);
    let domain = forceDomain || (meta && meta.domain) || null;
    // prefer configured domainPriority when multiple plausible domains (not fully detectable here)
    if (!domain && constantsLib && constantsLib.getDomainForValue) {
      domain = constantsLib.getDomainForValue(c.value);
    }
    let suggestedName = (meta && meta.name) || null;
    if (!suggestedName && typeof c.value === 'number') {
      // generic name from domain
      if (domain === 'astronomy' && Math.abs(c.value - 365.25) <= 1e-9) suggestedName = 'TROPICAL_YEAR_DAYS';
      else if (domain === 'time' && Math.abs(c.value - 86400) <= 1e-9) suggestedName = 'SECONDS_PER_DAY';
      else if (domain === 'time' && Math.abs(c.value - 1000) <= 1e-9) suggestedName = 'MS_PER_SECOND';
    }
    if (domain) domainHits.push(domain);
    out.push({ value: c.value, confidence: c.confidence, domain, suggestedName });
  }
  // domain summary
  const domCount = domainHits.reduce((acc,d)=>{ acc[d]=(acc[d]||0)+1; return acc; },{});
  return { result: out, warnings, domainSummary: domCount };
}

function reconcileAntiPatterns(found, mode) {
  const out = { forbiddenNames: [] };
  const warnings = [];
  const entries = Object.entries(found || {}).sort((a,b)=> b[1]-a[1]);
  const thr = mode === 'permissive' ? 5 : 3;
  for (const [name, count] of entries.slice(0, 10)) {
    if (count >= thr) out.forbiddenNames.push(name);
  }
  if (!out.forbiddenNames.length) warnings.push('No strong generic names found to forbid.');
  return { result: out, warnings };
}

function computeQualityScore(findings, sane) {
  const casing = findings.naming && findings.naming.casing || {};
  const total = Object.values(casing).reduce((a,b)=>a+b,0) || 1;
  const best = bestNamingStyle(casing);
  const majority = (casing[best] || 0) / total;
  const namingScore = Math.round(Math.min(1, Math.max(0, (majority - 0.4) / 0.6)) * 100);
  const booleanPrefixCounts = findings.naming && findings.naming.booleanPrefixes || { withPrefix: 0, without: 0 };
  const boolScore = Math.round(Math.min(1, (booleanPrefixCounts.withPrefix + 1) / (booleanPrefixCounts.withPrefix + booleanPrefixCounts.without + 1)) * 100);
  const genTotal = Object.values(findings.genericNames || {}).reduce((a,b)=>a+b,0);
  const genScore = Math.round(100 - Math.min(100, genTotal));
  const constHigh = (findings.constants || []).filter(c=> c.confidence >= (sane.minimumConfidence || 0.7)).length;
  const constScore = Math.min(100, 50 + constHigh * 10);
  const overall = Math.round((namingScore*0.3 + boolScore*0.2 + genScore*0.2 + constScore*0.3));
  return { overall, breakdown: { naming: namingScore, booleanPrefixes: boolScore, genericNames: genScore, constants: constScore }, notes: [] };
}

function reconcile(findings, sanityRules, options) {
  const sane = Object.assign({}, DEFAULT_SANITY, sanityRules || {});
  const warnings = [];
  const recommendations = [];
  const conflicts = [];

  const mode = (options && options.mode) || 'adaptive';
  const namingR = reconcileNaming(findings.naming || {}, sane, mode);
  warnings.push(...namingR.warnings);

  const constR = reconcileConstants(findings.constants || [], sane, options);
  warnings.push(...constR.warnings);

  const antiR = reconcileAntiPatterns(findings.genericNames || {}, mode);
  warnings.push(...(antiR.warnings||[]));

  const score = computeQualityScore(findings, sane);

  const reconciliation = {
    naming: namingR.result,
    antiPatterns: antiR.result,
    constantResolution: (options && options.config && options.config.constantResolution) || {},
  };

  return {
    domain: { constants: constR.result, summary: constR.domainSummary },
    result: reconciliation,
    warnings,
    recommendations,
    conflicts,
    score
  };
}

function generateDomain(result) {
  const items = (result && result.constants) || [];
  const lines = [
    "// Generated by 'learn' reconciliation",
    "'use strict';",
    'module.exports = {',
    '  constants: [',
    ...items.map(c => `    { value: ${c.value}, name: ${c.suggestedName ? `'${c.suggestedName}'` : 'null'}, domain: ${c.domain ? `'${c.domain}'` : 'null'} },`),
    '  ]',
    '};',
    ''
  ];
  return lines.join('\n');
}

module.exports = {
  DEFAULT_SANITY: DEFAULT_SANITY,
  reconcile,
  reconcileNaming,
  reconcileConstants,
  reconcileAntiPatterns,
  computeQualityScore,
  generateDomain
};

'use strict';

function deepFreeze(obj) {
  Object.freeze(obj);
  Object.getOwnPropertyNames(obj).forEach((prop) => {
    if (obj[prop] !== null && (typeof obj[prop] === 'object' || typeof obj[prop] === 'function') && !Object.isFrozen(obj[prop])) {
      deepFreeze(obj[prop]);
    }
  });
  return obj;
}

const DEFAULT_ARCHITECTURE = deepFreeze({
  fileStructure: {
    pattern: 'feature-based'
  },
  maxFileLength: {
    cli: 100,
    command: 150,
    util: 200,
    generator: 250,
    component: 300,
    default: 250
  },
  organization: {
    commands: 'lib/commands/{command}/',
    utils: 'lib/utils/',
    generators: 'lib/generators/',
    tests: 'tests/'
  },
  functions: {
    maxLength: 50,
    maxComplexity: 10,
    maxDepth: 4,
    maxParams: 4,
    maxStatements: 30,
    singlePurpose: true,
    preferPure: true
  },
  patterns: {
    cliStyle: 'orchestration-shell',
    errorHandling: 'explicit',
    asyncStyle: 'async-await'
  }
});

/**
 * Merge user architecture config with defaults
 * @param {object} userArch - User-provided architecture config
 * @returns {object} Merged architecture config
 */
function mergeArchitecture(userArch) {
  if (!userArch || typeof userArch !== 'object') {
    return JSON.parse(JSON.stringify(DEFAULT_ARCHITECTURE));
  }

  const merged = JSON.parse(JSON.stringify(DEFAULT_ARCHITECTURE));

  // Merge fileStructure
  if (userArch.fileStructure && typeof userArch.fileStructure === 'object') {
    Object.assign(merged.fileStructure, userArch.fileStructure);
  }

  // Merge maxFileLength
  if (userArch.maxFileLength && typeof userArch.maxFileLength === 'object') {
    Object.assign(merged.maxFileLength, userArch.maxFileLength);
  }

  // Merge organization
  if (userArch.organization && typeof userArch.organization === 'object') {
    Object.assign(merged.organization, userArch.organization);
  }

  // Merge functions
  if (userArch.functions && typeof userArch.functions === 'object') {
    Object.assign(merged.functions, userArch.functions);
  }

  // Merge patterns
  if (userArch.patterns && typeof userArch.patterns === 'object') {
    Object.assign(merged.patterns, userArch.patterns);
  }

  return merged;
}

module.exports = {
  DEFAULT_ARCHITECTURE,
  mergeArchitecture
};

'use strict';

function normalizeBoolean(val, defaultValue) {
  if (val === undefined || val === null) return defaultValue;
  if (typeof val === 'boolean') return val;
  if (typeof val === 'number') return val !== 0;
  if (typeof val === 'string') {
    const s = val.trim().toLowerCase();
    if (s === 'true' || s === '1' || s === 'on' || s === 'yes' || s === 'y') return true;
    if (s === 'false' || s === '0' || s === 'off' || s === 'no' || s === 'n') return false;
  }
  return defaultValue;
}

/**
 * Decide whether architecture guardrails should be enabled.
 * Rules:
 *  - Enabled by default
 *  - --no-arch disables (highest precedence)
 *  - --arch=false disables
 *  - --arch (or --arch=true) enables (unless --no-arch also present)
 */
function shouldEnableArchitecture(args) {
  const a = args || {};
  const hasNoArch = a['no-arch'] !== undefined;
  const noArch = normalizeBoolean(a['no-arch'], false);
  if (hasNoArch && noArch) return false; // explicit --no-arch

  if (Object.prototype.hasOwnProperty.call(a, 'arch')) {
    const archVal = normalizeBoolean(a.arch, true);
    if (!archVal) return false; // --arch=false
    if (archVal) return true;   // --arch or --arch=true
  }

  // default
  return true;
}

module.exports = { shouldEnableArchitecture, normalizeBoolean };

'use strict';

const fs = require('fs');
const path = require('path');
const crypto = require('crypto');
const { validateConstantsPackage } = require('./validate-constants-package');

function readJson(p) { try { return JSON.parse(fs.readFileSync(p, 'utf8')); } catch { return null; } }
function sha1(str) { return crypto.createHash('sha1').update(String(str)).digest('hex'); }
const CACHE = { npm: new Map() };

function loadBuiltinConstants() {
  const constantsDir = path.join(__dirname, '..', 'constants');
  const out = {};
  try {
    const files = fs.readdirSync(constantsDir).filter((f) => f.endsWith('.js'));
    for (const f of files) {
      const mod = require(path.join(constantsDir, f));
      const domain = f.replace(/\.js$/, '');
      out[domain] = { domain, version: '1.0.0', constants: mod.constants || [], terms: { entities: [], properties: [], actions: [] }, source: 'builtin' };
    }
  } catch (err) {
    if (process.env.DEBUG_AI_CONSTANTS) {

      console.warn(`[discover-constants] failed to read builtin constants: ${err && err.message}`);
    }
  }
  return out;
}

function discoverNpmPackages(projectRoot, allowlist) {
const pkgPath = path.join(projectRoot, 'package.json');
  const pkg = readJson(pkgPath) || {};
  const all = { ...(pkg.dependencies || {}), ...(pkg.devDependencies || {}) };
  const patterns = [/^@ai-constants\//, /^eslint-constants-/, /-ai-constants$/];
  const out = {};
try {
    const depsString = JSON.stringify({ d: pkg.dependencies || {}, dv: pkg.devDependencies || {} });
    const key = projectRoot + ':' + sha1(depsString);
    if (CACHE.npm.has(key) && !process.env.AI_CONSTANTS_NO_CACHE) {
      return CACHE.npm.get(key);
    }
  } catch (e) {
    if (process.env.DEBUG_AI_CONSTANTS) {

      console.warn(`[discover-constants] cache key compute failed: ${e && e.message}`);
    }
  }
  for (const name of Object.keys(all)) {
if (!patterns.some((p) => p.test(name))) continue;
    if (Array.isArray(allowlist) && allowlist.length) {
      const allowed = allowlist.some((rule) => {
        if (!rule) return false;
        if (rule.startsWith('^')) { try { return new RegExp(rule).test(name); } catch { return false; } }
        return rule === name;
      });
      if (!allowed) continue;
    }
    try {
      const mod = require(name);
      const packageMeta = mod && (mod.default || mod);
      validateConstantsPackage(packageMeta);
      out[packageMeta.domain] = { ...packageMeta, source: 'npm', package: name };
    } catch (err) {
      // skip invalid packages but log in debug
      if (process.env.DEBUG_AI_CONSTANTS) {

        console.warn(`[discover-constants] failed to load ${name}: ${err && err.message}`);
      }
    }
  }
try {
    const depsString = JSON.stringify({ d: pkg.dependencies || {}, dv: pkg.devDependencies || {} });
    const key = projectRoot + ':' + sha1(depsString);
    CACHE.npm.set(key, out);
  } catch (e) {
    if (process.env.DEBUG_AI_CONSTANTS) {

      console.warn(`[discover-constants] cache set failed: ${e && e.message}`);
    }
  }
  return out;
}

function discoverLocalFiles(projectRoot) {
  const dir = path.join(projectRoot, '.ai-constants');
  const out = {};
  if (!fs.existsSync(dir)) return out;
  for (const f of fs.readdirSync(dir)) {
    if (!f.endsWith('.js')) continue;
    try {
      const mod = require(path.join(dir, f));
      const packageMeta = mod && (mod.default || mod);
      validateConstantsPackage(packageMeta);
      out[packageMeta.domain] = { ...packageMeta, source: 'local', file: f };
    } catch (err) {
      if (process.env.DEBUG_AI_CONSTANTS) {

        console.warn(`[discover-constants] failed to load ${f}: ${err && err.message}`);
      }
    }
  }
  return out;
}

function loadCustomConstants(projectRoot) {
  const p = path.join(projectRoot, '.ai-coding-guide.json');
  const cfg = readJson(p) || {};
  const custom = cfg.customConstants || {};
  const out = {};
  for (const [domain, list] of Object.entries(custom)) {
    out[domain] = { domain, version: '1.0.0', constants: list || [], source: 'custom' };
  }
  return out;
}

function discoverConstants(projectRoot = process.cwd()) {
const cfg = readJson(path.join(projectRoot, '.ai-coding-guide.json')) || {};
  const allowlist = Array.isArray(cfg.externalConstantsAllowlist) ? cfg.externalConstantsAllowlist : [];
  return {
    builtin: loadBuiltinConstants(),
    npm: discoverNpmPackages(projectRoot, allowlist),
    local: discoverLocalFiles(projectRoot),
    custom: loadCustomConstants(projectRoot)
  };
}

module.exports = {
  discoverConstants,
  loadBuiltinConstants,
  discoverNpmPackages,
  discoverLocalFiles,
  loadCustomConstants
};

'use strict';

// Lightweight cache keyed by SourceCode instance
const cache = new WeakMap();

function getDomainComments(context) {
  const src = context.getSourceCode();
  let entry = cache.get(src);
  if (!entry) {
    const all = src.getAllComments ? src.getAllComments() : [];
    const domainComments = [];
    for (const c of all || []) {
      const val = String(c.value || '');
      const m1 = val.match(/@domain\s+([A-Za-z0-9_-]+)/);
      const m2 = val.match(/@domains?\s+([A-Za-z0-9_,\s-]+)/);
      if (m1) domainComments.push({ type: 'single', domain: m1[1], loc: c.loc });
      else if (m2) domainComments.push({ type: 'multi', domains: m2[1].split(',').map(s=>s.trim()).filter(Boolean), loc: c.loc });
    }
    entry = { domainComments };
    cache.set(src, entry);
  }
  return entry.domainComments;
}

function getNearestSectionDomain(node, context) {
  try {
    if (!node.loc) return null;
    const line = node.loc.start.line;
    let nearest = null;
    for (const dc of getDomainComments(context)) {
      if (dc.type === 'single' && dc.loc && dc.loc.end && dc.loc.end.line < line) {
        if (!nearest || dc.loc.end.line > nearest.loc.end.line) nearest = dc;
      }
    }
    return nearest ? nearest.domain : null;
  } catch {
    return null;
  }
}

function getDomainAnnotation(node, context) {
  const src = context.getSourceCode();
  const comments = src.getCommentsBefore(node) || [];
  for (const c of comments) {
    const m = String(c.value || '').match(/@domain\s+([A-Za-z0-9_-]+)/);
    if (m) return m[1];
  }
  // Fallback to nearest section-level @domain above this node
  const section = getNearestSectionDomain(node, context);
  if (section) return section;
  return null;
}

function getFileDomains(context) {
  // Use cached parsed comments and look for first multi-domain declaration
  for (const dc of getDomainComments(context)) {
    if (dc.type === 'multi') return dc.domains;
  }
  return [];
}

function hasInlineDomainTag(node, context) {
  const src = context.getSourceCode();
  const text = src.getText(node) + (src.getCommentsAfter(node)?.map((c) => c.value).join(' ') || '');
  return /@domain\s+[A-Za-z0-9_-]+/.test(text);
}

module.exports = {
  getDomainAnnotation,
  getFileDomains,
  hasInlineDomainTag
};

'use strict';

const path = require('path');

function loadFingerprint(cwd) {
  const fp = path.join(cwd, '.ai-constants', 'project-fingerprint.js');
  try {
    const mod = require(fp);
    if (!mod || !Array.isArray(mod.constants)) return null;
    return mod.constants;
  } catch {
    return null;
  }
}

function applyFingerprintToConfig(cwd, cfg) {
  const items = loadFingerprint(cwd);
  if (!items || !items.length) return;
  cfg.constantResolution = cfg.constantResolution || {};
  const seenDomains = new Set(cfg.domains.additional || []);
  for (const c of items) {
    if (c && typeof c.value === 'number' && c.domain) {
      cfg.constantResolution[String(c.value)] = c.domain;
      if (c.domain !== cfg.domains.primary && !seenDomains.has(c.domain)) {
        cfg.domains.additional.push(c.domain);
        seenDomains.add(c.domain);
      }
    }
  }
}

module.exports = { loadFingerprint, applyFingerprintToConfig };

'use strict';

function mergeConstants(discovered) {
  const merged = {};
  const sources = [discovered.builtin || {}, discovered.npm || {}, discovered.local || {}, discovered.custom || {}];
  for (const src of sources) {
    for (const [domain, data] of Object.entries(src)) {
      if (!merged[domain]) {
        merged[domain] = { domain, constants: [], terms: {}, naming: {}, sources: [] };
      }
      const acc = merged[domain];
      const byValue = new Map(acc.constants.map((c, i) => [String(c.value ?? c), i]));
      for (const c of (data.constants || [])) {
        const key = String(c.value ?? c);
        if (byValue.has(key)) {
          acc.constants[byValue.get(key)] = c; // override
        } else {
          acc.constants.push(c);
          byValue.set(key, acc.constants.length - 1);
        }
      }
      if (data.terms) {
        acc.terms.entities = [ ...(acc.terms.entities || []), ...((data.terms.entities) || []) ];
        acc.terms.properties = [ ...(acc.terms.properties || []), ...((data.terms.properties) || []) ];
        acc.terms.actions = [ ...(acc.terms.actions || []), ...((data.terms.actions) || []) ];
      }
      if (data.naming) {
        acc.naming = { ...acc.naming, ...data.naming };
      }
      acc.sources.push({ type: data.source || 'builtin', package: data.package || data.file || 'builtin' });
    }
  }
  return merged;
}

module.exports = { mergeConstants };

'use strict';

const fs = require('fs');
const path = require('path');
const Ajv = require('ajv');

const DEFAULTS = Object.freeze({
  domain: 'general',
  domains: { primary: 'general', additional: [] },
  domainPriority: [],
  constantResolution: {},
  version: '1.0',
  constants: {},
  terms: { entities: [], properties: [], actions: [] },
  naming: { style: 'camelCase', booleanPrefix: ['is','has','should','can'], asyncPrefix: ['fetch','load','save'], pluralizeCollections: true },
antiPatterns: { forbiddenNames: [], forbiddenTerms: [] },
  experimentalExternalConstants: true,
  externalConstantsAllowlist: []
});

let cache = null; // { file, mtimeMs, config }

function tryStat(p) { try { return fs.statSync(p); } catch { return null; } }
function tryRead(p) { try { return fs.readFileSync(p, 'utf8'); } catch { return null; } }

function shallowValidate(obj) {
  if (!obj || typeof obj !== 'object') return false;
  if (obj.constants && typeof obj.constants !== 'object') return false;
  if (obj.terms && typeof obj.terms !== 'object') return false;
  if (obj.naming && typeof obj.naming !== 'object') return false;
  if (obj.antiPatterns && typeof obj.antiPatterns !== 'object') return false;
  return true;
}

function deepMerge(base, override) {
  if (override === null || override === undefined) return base;
  const out = Array.isArray(base) ? base.slice() : { ...base };
  for (const [k, v] of Object.entries(override)) {
    if (v && typeof v === 'object' && !Array.isArray(v) && base && typeof base[k] === 'object' && !Array.isArray(base[k])) {
      out[k] = deepMerge(base[k], v);
    } else {
      out[k] = v;
    }
  }
  return out;
}

function findConfig(startDir) {
  let dir = startDir;
  for (let i = 0; i < 20; i++) {
    const candidate = path.join(dir, '.ai-coding-guide.json');
    if (fs.existsSync(candidate)) return candidate;
    const parent = path.dirname(dir);
    if (parent === dir) break;
    dir = parent;
  }
  return null;
}

let ajv = null;
let validate = null;
const lastWarn = null; // { file, mtimeMs }

function ensureValidator(schemaPath) {
  if (!ajv) ajv = new Ajv({ allErrors: true, strict: false });
  if (!validate) {
    try {
      const schema = require(schemaPath);
      validate = ajv.compile(schema);
    } catch {
      validate = null;
    }
  }
}

function readProjectConfig(context) {
  const filename = typeof context.getFilename === 'function' ? context.getFilename() : '';
  const startDir = filename ? path.dirname(filename) : (typeof context.getCwd === 'function' ? context.getCwd() : process.cwd());

  // 1) Base from disk (cached)
  let fileMerged = DEFAULTS;
  const file = findConfig(startDir);
  if (file) {
    const st = tryStat(file);
    if (st) {
      if (cache && cache.file === file && cache.mtimeMs === st.mtimeMs) {
        fileMerged = cache.config;
      } else {
        const raw = tryRead(file);
        if (raw) {
          let parsed;
          try { parsed = JSON.parse(raw); } catch { parsed = null; }
          if (parsed && shallowValidate(parsed)) {
            // JSON Schema validation (best-effort)
            const schemaPath = path.resolve(__dirname, '..', 'config', 'ai-coding-guide.schema.json');
            ensureValidator(schemaPath);
            if (!validate || validate(parsed)) {
              fileMerged = deepMerge(DEFAULTS, parsed);
              // Derive domainPriority from file config
              try {
                const pri = Array.isArray(fileMerged.domainPriority) ? fileMerged.domainPriority.slice() : [];
                const primary = fileMerged.domains && fileMerged.domains.primary ? [fileMerged.domains.primary] : (fileMerged.domain ? [fileMerged.domain] : []);
                const additional = fileMerged.domains && Array.isArray(fileMerged.domains.additional) ? fileMerged.domains.additional : [];
                const combined = [...primary, ...additional, ...pri].filter(Boolean);
                const seen = new Set();
                fileMerged.domainPriority = combined.filter((d) => (d && !seen.has(d) && seen.add(d)));
              } catch { /* ignore derivation errors for file config */ }
              cache = { file, mtimeMs: st.mtimeMs, config: fileMerged };
            }
          }
        }
      }
    }
  }

  // 2) Overlay from env
  let envCfg = null;
  const envRaw = process.env.AI_SNIFFTEST_CONFIG_JSON;
  if (envRaw) {
    try {
      const parsedEnv = JSON.parse(envRaw);
      if (parsedEnv && typeof parsedEnv === 'object') envCfg = parsedEnv;
    } catch { /* ignore env parse */ }
  }

  // 3) Overlay from settings
  let settingsCfg = null;
  try {
    const s = context && context.settings && context.settings['ai-code-snifftest'];
    if (s && typeof s === 'object') settingsCfg = s;
  } catch { /* ignore settings extraction */ }

  // 4) Merge in precedence: disk < env < settings
  let finalCfg = fileMerged;
  if (envCfg) finalCfg = deepMerge(finalCfg, envCfg);
  if (settingsCfg) finalCfg = deepMerge(finalCfg, settingsCfg);

  // 5) Derive domainPriority again after overlays
  try {
    const pri = Array.isArray(finalCfg.domainPriority) ? finalCfg.domainPriority.slice() : [];
    const primary = finalCfg.domains && finalCfg.domains.primary ? [finalCfg.domains.primary] : (finalCfg.domain ? [finalCfg.domain] : []);
    const additional = finalCfg.domains && Array.isArray(finalCfg.domains.additional) ? finalCfg.domains.additional : [];
    const combined = [...primary, ...additional, ...pri].filter(Boolean);
    const seen = new Set();
    finalCfg.domainPriority = combined.filter((d) => (d && !seen.has(d) && seen.add(d)));
  } catch { /* ignore final derivation */ }

  return finalCfg;
}

function loadProjectConfigFile(cwd) {
  const file = path.join(cwd, '.ai-coding-guide.json');
  try {
    const raw = fs.readFileSync(file, 'utf8');
    return { file, json: JSON.parse(raw) };
  } catch {
    return { file, json: DEFAULTS };
  }
}

function writeProjectConfigFile(file, json) {
  fs.writeFileSync(file, JSON.stringify(json, null, 2) + '\n');
}

module.exports = {
  readProjectConfig,
  DEFAULTS,
  deepMerge,
  loadProjectConfigFile,
  writeProjectConfigFile
};

/**
 * Detect project type and tech stack from package.json
 * @param {string} cwd - Project root directory
 * @returns {Object} Project context with type, domains, description, techStack
 */
function detectProjectContext(cwd) {
  const fs = require('fs');
  const path = require('path');

  try {
    const pkgPath = path.join(cwd, 'package.json');
    if (!fs.existsSync(pkgPath)) {
      return { type: 'general', domains: ['cs'], description: 'Software project', techStack: [] };
    }

    const pkg = JSON.parse(fs.readFileSync(pkgPath, 'utf8'));
    const deps = { ...pkg.dependencies, ...pkg.devDependencies };
    const techStack = [];

    // Detect Node.js version
    if (pkg.engines && pkg.engines.node) {
      techStack.push(`Node.js ${pkg.engines.node}`);
    }

    // ESLint plugin?
    if (pkg.name && pkg.name.includes('eslint-plugin')) {
      techStack.push('ESLint 9+');
      return {
        type: 'dev-tools',
        domains: ['cs', 'dev-tools', 'linting'],
        description: 'ESLint plugin',
        techStack
      };
    }

    // CLI tool?
    if (pkg.bin) {
      techStack.push('CLI');
      return {
        type: 'cli',
        domains: ['cs', 'cli', 'node-ecosystem'],
        description: 'CLI tool',
        techStack
      };
    }

    // React web app?
    if (deps.react) {
      techStack.push(`React ${deps.react}`);
      if (deps.next) techStack.push('Next.js');
      return {
        type: 'web-app',
        domains: ['cs', 'web', 'ui'],
        description: 'React web application',
        techStack
      };
    }

    // Vue app?
    if (deps.vue) {
      techStack.push(`Vue ${deps.vue}`);
      return {
        type: 'web-app',
        domains: ['cs', 'web', 'ui'],
        description: 'Vue web application',
        techStack
      };
    }

    // Express/API server?
    if (deps.express || deps.koa || deps.fastify) {
      techStack.push('API server');
      return {
        type: 'api-server',
        domains: ['cs', 'backend', 'api'],
        description: 'API server',
        techStack
      };
    }

    // Library/package?
    if (pkg.main && !pkg.bin && !deps.react && !deps.vue) {
      return {
        type: 'library',
        domains: ['cs', 'library'],
        description: 'JavaScript library',
        techStack
      };
    }

    // Default
    return {
      type: 'general',
      domains: ['cs'],
      description: 'Software project',
      techStack
    };
  } catch (err) {
    return {
      type: 'general',
      domains: ['cs'],
      description: 'Software project',
      techStack: [],
      error: err.message
    };
  }
}

module.exports = { detectProjectContext };

