// Operator Precedence - Correctness is non-negotiable
const invalidOperatorPrecedence = [
  // Mixed operators (multiplication before addition)
  {
    code: 'const x = 2 + 3 * 4;',
    errors: [{ messageId: 'redundantCalculation' }],
    output: 'const x = 14;' // NOT 20!
  },
  {
