// Scientific formulas that should be skipped (no report)
const validScientificFormulas = [
  // High precision results
  { code: 'const meanMotion = 360 / 365.25;' },
  { code: 'const nodeMotionPerDay = -19.3416 / 365.25;' },
  { code: 'const daysPerSaros = 223 * 29.53059;' },

  // Physical constants present in expression
  { code: 'const yearMs = 365.25 * 24 * 3600 * 1000;' },
  { code: 'const centuryMs = 100 * 365.25 * 24 * 3600 * 1000;' },

  // Scientific variable names in owning identifier
  { code: 'const meanLongitude = 1 + 2;' },
  { code: 'const orbitalPeriod = 3 * 4;' },
  { code: 'const nodeMotion = 5 / 6;' },

];

//------------------------------------------------------------------------------
// Run All Tests
//------------------------------------------------------------------------------

// Suite A: explicit settings  experimentalExternalConstants: true (baseline behavior)
(function runSuiteExtConstTrue(){
  const injectSettings = (tc) => Object.assign({}, tc, { settings: { 'ai-code-snifftest': { experimentalExternalConstants: true } } });
  ruleTester.run('no-redundant-calculations [extConst=true]', rule, {
    valid: [
      ...validBasicTests.map(injectSettings),
      ...validNumericBoundaries.map(injectSettings),
      ...validFalsePositives.map(injectSettings),
      ...validModernJS.map(injectSettings),
      ...validEdgeCoverage.map(injectSettings),
      ...validScientificFormulas.map(injectSettings),
      ...validV111Additions.map(injectSettings)
    ],
    invalid: [
      ...invalidBasicTests.map(injectSettings),
      ...invalidNumericBoundaries.map(injectSettings),
      ...invalidOperatorPrecedence.map(injectSettings),
      ...invalidModernJS.map(injectSettings),
      ...invalidBranchCoverage.map(injectSettings),
      ...invalidMultiInstance.map(injectSettings),
      ...invalidMultiInstanceMixed.map(injectSettings)
    ]
  });
})();

// Suite B: explicit settings  experimentalExternalConstants: false (deterministic against disk)
(function runSuiteExtConstFalse(){
  const injectSettings = (tc) => Object.assign({}, tc, { settings: { 'ai-code-snifftest': { experimentalExternalConstants: false } } });
  ruleTester.run('no-redundant-calculations [extConst=false]', rule, {
    valid: [ ],
    invalid: [
      injectSettings({
        code: 'const result = 5 * 4 * 3;',
        errors: [{ messageId: 'redundantCalculation' }],
        output: 'const result = 60;'
      })
    ]
  });
})();

/**
 * @fileoverview Simplify redundant conditional expressions
 * @author mojoatomic
 */
'use strict';

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

const rule = require('../../../lib/rules/no-redundant-conditionals'),
  RuleTester = require('eslint').RuleTester;


//------------------------------------------------------------------------------
// Tests
//------------------------------------------------------------------------------

const ruleTester = new RuleTester({ languageOptions: { ecmaVersion: 2021, sourceType: 'module' } });

//------------------------------------------------------------------------------
// Basic Functionality Tests
//------------------------------------------------------------------------------

const validBasicTests = [
    // Normal conditions
    'if (x) { doSomething(); }',
    'function test() { if (x > 5) { return true; } }',
    'if (condition && other) { }',

    // Variables (not constants)
    'if (value) { doWork(); }',
    'const result = x ? getValue() : getOther();',

    // Ternaries with different values
    'const x = condition ? 1 : 2;',
    'const y = test ? "yes" : "no";',

    // Number/string comparisons (not boolean)
    'if (x === 1) { }',
    'if (value === "true") { }',
];

//------------------------------------------------------------------------------
// Phase 1: Critical Edge Cases
//------------------------------------------------------------------------------

// Falsy Values - Different falsy values have different semantics
const validFalsyValues = [
  // null check (intentional)
  'if (x === null) { handleNull(); }',
  'if (value !== null) { useValue(); }',

  // undefined check (intentional)
  'if (x === undefined) { handleUndefined(); }',
  'if (typeof x !== "undefined") { }',

  // NaN check (special case)
  'if (Number.isNaN(x)) { }',
  'if (x !== x) { }', // NaN !== NaN pattern

  // Empty string check (intentional)
  'if (str === "") { handleEmpty(); }',
  'if (str !== "") { handleNonEmpty(); }',
];

const invalidFalsyValues = [
  // Null as constant condition
  {
    code: 'if (null) { unreachable(); }',
    errors: [{ messageId: 'constantCondition' }],
    output: '',
  },
  // Undefined as constant condition
  {
    code: 'if (undefined) { unreachable(); }',
    errors: [{ messageId: 'constantCondition' }],
    output: '',
  },
  // NaN as constant condition
  {
    code: 'if (NaN) { unreachable(); }',
    errors: [{ messageId: 'constantCondition' }],
    output: '',
  },
];

// Truthy Values - Non-boolean truthy constants
const invalidTruthyValues = [
  // Non-empty string
  {
    code: 'if ("hello") { alwaysRuns(); }',
    errors: [{ messageId: 'constantCondition' }],
    output: 'alwaysRuns();',
  },
  // Positive number
  {
    code: 'if (42) { alwaysRuns(); }',
    errors: [{ messageId: 'constantCondition' }],
    output: 'alwaysRuns();',
  },
  // Negative number
  {
    code: 'if (-1) { alwaysRuns(); }',
    errors: [{ messageId: 'constantCondition' }],
    output: 'alwaysRuns();',
  },
  // Object literal
  {
