gen.next();
    `,
  },
  // Async generator
  {
    code: `
async function* asyncGen() {
  yield await Promise.resolve(1);
}
const ag = asyncGen();
ag.next();
    `,
  },
];

// IIFE Patterns - Immediately Invoked Function Expressions
const validIIFE = [
  // Classic IIFE
  {
    code: `
(function() {
  console.log('IIFE');
})();
    `,
  },
  // Arrow IIFE
  {
    code: `
(() => {
  return 42;
})();
    `,
  },
];

// Different Scopes - Functions called in different contexts
const validDifferentScopes = [
  // Function called in different scopes
  {
    code: `
function helper(x) { return x * 2; }
if (true) { helper(5); }
else { helper(10); }
    `,
  },
  // Function called in loop
  {
    code: `
function process(item) { return item.toUpperCase(); }
const items = ['a', 'b'];
for (const item of items) { process(item); }
    `,
  },
];

// Exported Functions - Public API should not trigger
const validExportedFunctions = [
  // Named export
  {
    code: `
export function wrapper(x) {
  return compute(x);
}
function compute(x) { return x * 2; }
wrapper(5);
    `,
  },
  // Default export
  {
    code: `
export default function wrapper(x) {
  return compute(x);
}
function compute(x) { return x * 2; }
wrapper(5);
    `,
  },
  // Export const (arrow)
  {
    code: `
export const wrapper = (x) => compute(x);
function compute(x) { return x * 2; }
wrapper(5);
    `,
  },
  // Export const (function expression) - should be skipped by rule (public API)
  {
    code: `
export const wrapper = function(x) {
  return compute(x);
};
function compute(x) { return x * 2; }
wrapper(5);
    `,
  },
];

// Side Effects - Functions with side effects should not be inlined
const validSideEffects = [
  // Console logging
  {
    code: `
function logAndCompute(x) {
  console.log('computing:', x);
  return compute(x);
}
function compute(x) { return x * 2; }
logAndCompute(5);
    `,
  },
  // State mutation
  {
    code: `
let counter = 0;
function trackAndCompute(x) {
  counter++;
  return compute(x);
}
function compute(x) { return x * 2; }
trackAndCompute(5);
    `,
  },
  // Multiple statements
  {
    code: `
function validate(x) {
  if (x < 0) throw new Error('negative');
  return compute(x);
}
function compute(x) { return x * 2; }
validate(5);
    `,
  },
];

// Hoisting Edge Cases - Function declarations vs expressions
const validHoisting = [
  // Called before declaration (hoisting)
  {
    code: `
helper(5);
function helper(x) { return x * 2; }
    `,
  },
  // Function expression not hoisted
  {
    code: `
const helper = function(x) { return x * 2; };
helper(5);
helper(10);
    `,
  },
];

// Special Arguments - arguments object, new.target
const validSpecialArguments = [
  // Uses arguments object
  {
    code: `
function wrapper() {
  return compute(arguments[0]);
}
function compute(x) { return x * 2; }
wrapper(5);
    `,
  },
  // Uses new.target
  {
    code: `
function Wrapper(x) {
  if (!new.target) throw new Error('use new');
  return compute(x);
}
function compute(x) { return x * 2; }
new Wrapper(5);
    `,
  },
];

// Function Properties - Functions used as objects
const validFunctionProperties = [
  // Function has properties attached
  {
    code: `
function wrapper(x) { return compute(x); }
wrapper.version = '1.0';
function compute(x) { return x * 2; }
wrapper(5);
    `,
  },
  // Function passed as callback
  {
    code: `
function wrapper(x) { return compute(x); }
function compute(x) { return x * 2; }
[1, 2, 3].map(wrapper);
    `,
  },
];

// Conditional/Try-Catch Wrappers - Error handling
const validErrorHandling = [
  // Try-catch wrapper
  {
    code: `
function safeCompute(x) {
  try {
    return compute(x);
  } catch (e) {
    return 0;
  }
}
function compute(x) { return x * 2; }
safeCompute(5);
    `,
  },
  // Conditional wrapper
  {
    code: `
function conditionalCompute(x) {
  return x > 0 ? compute(x) : 0;
}
function compute(x) { return x * 2; }
conditionalCompute(5);
    `,
  },
];

// Name Collisions - Same parameter names, different semantics
const validNameCollisions = [
  // Parameter shadows outer variable (used multiple times)
  {
    code: `
const x = 10;
function wrapper(x) {
  return compute(x);
}
function compute(x) { return x * 2; }
wrapper(5);
wrapper(10);
    `,
  },
  // Wrapper provides semantic clarity
  {
    code: `
function calculateTotalPrice(price) {
  return addTax(price);
}
function addTax(amount) { return amount * 1.08; }
calculateTotalPrice(100);
calculateTotalPrice(200);
    `,
  },
];

const invalidBasicTests = [
    // Basic trivial wrapper - function declaration
    {
      code: `function calculateTax(amount) {
  return getTax(amount);
}
function getTax(amount) {
  return amount * 0.08;
}
calculateTax(100);`,
      errors: [{
        messageId: 'unnecessaryWrapper',
        data: { name: 'calculateTax', wrappedName: 'getTax' },
        suggestions: [{
          messageId: 'inlineFunction',
          output: `function getTax(amount) {
  return amount * 0.08;
}
calculateTax(100);`,
        }],
      }],
    },

    // Trivial wrapper - arrow function in variable
    {
      code: `const wrapper = (x) => {
  return compute(x);
};
function compute(x) { return x * 2; }
wrapper(5);`,
      errors: [{
        messageId: 'unnecessaryWrapper',
        data: { name: 'wrapper', wrappedName: 'compute' },
        suggestions: [{
          messageId: 'inlineFunction',
          output: `function compute(x) { return x * 2; }
wrapper(5);`,
        }],
      }],
    },

    // Multiple parameters
    {
      code: `function add(a, b) {
  return sum(a, b);
}
function sum(x, y) { return x + y; }
add(1, 2);`,
      errors: [{
        messageId: 'unnecessaryWrapper',
        data: { name: 'add', wrappedName: 'sum' },
        suggestions: [{
          messageId: 'inlineFunction',
          output: `function sum(x, y) { return x + y; }
add(1, 2);`,
        }],
      }],
    },

    // Zero parameters
    {
      code: `function getConfig() {
  return loadConfig();
}
function loadConfig() { return {}; }
getConfig();`,
      errors: [{
        messageId: 'unnecessaryWrapper',
        data: { name: 'getConfig', wrappedName: 'loadConfig' },
        suggestions: [{
          messageId: 'inlineFunction',
          output: `function loadConfig() { return {}; }
getConfig();`,
        }],
      }],
    },

    // Method call wrapper - removed (too complex for this rule)

    // Chained method call wrapper - removed (too complex for this rule)

    // Function expression assigned to variable
    {
      code: `const wrapper = function(x) {
  return helper(x);
};
function helper(x) { return x * 2; }
wrapper(5);`,
      errors: [{
        messageId: 'unnecessaryWrapper',
        data: { name: 'wrapper', wrappedName: 'helper' },
        suggestions: [{
          messageId: 'inlineFunction',
          output: `function helper(x) { return x * 2; }
wrapper(5);`,
        }],
      }],
    },

    // Single parameter with descriptive name
    {
      code: `function formatUser(user) {
  return display(user);
}
function display(obj) { return JSON.stringify(obj); }
formatUser({ name: 'Alice' });`,
      errors: [{
        messageId: 'unnecessaryWrapper',
        data: { name: 'formatUser', wrappedName: 'display' },
        suggestions: [{
          messageId: 'inlineFunction',
          output: `function display(obj) { return JSON.stringify(obj); }
formatUser({ name: 'Alice' });`,
        }],
      }],
    },
];

// Fixer Whitespace/Suggestion Output - Validate fixer trimming behavior
const invalidFixerWhitespace = [
  // 1) Same-line preceding code: left-trim eats preceding space; newline after wrapper is removed
  {
    code: 'const a = 1; function wrapper(x) { return compute(x); }\nfunction compute(x) { return x * 2; }\nwrapper(5);',
    errors: [{
      messageId: 'unnecessaryWrapper',
      data: { name: 'wrapper', wrappedName: 'compute' },
      suggestions: [{
        messageId: 'inlineFunction',
        // Note: space before function is removed; newline after wrapper is removed
        output: 'const a = 1;function compute(x) { return x * 2; }\nwrapper(5);'
      }]
    }],
  },
  // 2) Trailing spaces + newline after wrapper: right-trim eats spaces and one newline
  {
    code: 'function wrapper(x) { return compute(x); }    \nfunction compute(x) { return x * 2; }\nwrapper(5);',
    errors: [{
      messageId: 'unnecessaryWrapper',
      data: { name: 'wrapper', wrappedName: 'compute' },
      suggestions: [{
        messageId: 'inlineFunction',
        output: 'function compute(x) { return x * 2; }\nwrapper(5);'
      }]
    }],
  },
  // 3) Clean case: function at start of line
  {
    code: 'function wrapper(x) { return compute(x); }\nfunction compute(x) { return x * 2; }\nwrapper(5);',
    errors: [{
      messageId: 'unnecessaryWrapper',
      data: { name: 'wrapper', wrappedName: 'compute' },
      suggestions: [{
        messageId: 'inlineFunction',
        output: 'function compute(x) { return x * 2; }\nwrapper(5);'
      }]
    }],
  },
  // 4) Next token on same line (no newline): right-trim eats spaces but not next token
  {
    code: 'function wrapper(x) { return compute(x); }   console.log(\'x\');\nfunction compute(x) { return x * 2; }\nwrapper(5);',
    errors: [{
      messageId: 'unnecessaryWrapper',
      data: { name: 'wrapper', wrappedName: 'compute' },
      suggestions: [{
        messageId: 'inlineFunction',
        output: 'console.log(\'x\');\nfunction compute(x) { return x * 2; }\nwrapper(5);'
      }]
    }],
  },
  // 5) CRLF handling: trailing CRLF is removed as a single newline by the fixer loop
  {
    code: 'function wrapper(x) { return compute(x); }\r\nfunction compute(x) { return x * 2; }\nwrapper(5);',
    errors: [{
      messageId: 'unnecessaryWrapper',
      data: { name: 'wrapper', wrappedName: 'compute' },
      suggestions: [{
        messageId: 'inlineFunction',
        output: 'function compute(x) { return x * 2; }\nwrapper(5);'
      }]
    }],
  },
];

// Multi-instance: multiple trivial wrappers in one file (suggestions only)
const invalidMultiInstance = [
  {
    code: `
function w1(x) { return compute(x); }
function w2(y) { return compute(y); }
function compute(z) { return z * 2; }
w1(1); w2(2);
`,
    errors: [
      {
        messageId: 'unnecessaryWrapper',
        data: { name: 'w1', wrappedName: 'compute' },
        suggestions: [{
          messageId: 'inlineFunction',
          output: `
function w2(y) { return compute(y); }
function compute(z) { return z * 2; }
w1(1); w2(2);
`
        }]
      },
      {
        messageId: 'unnecessaryWrapper',
        data: { name: 'w2', wrappedName: 'compute' },
        suggestions: [{
          messageId: 'inlineFunction',
          output: `
function w1(x) { return compute(x); }
function compute(z) { return z * 2; }
w1(1); w2(2);
`
        }]
      }
    ]
  }
];

//------------------------------------------------------------------------------
// Run All Tests
//------------------------------------------------------------------------------

ruleTester.run('no-unnecessary-abstraction', rule, {
  valid: [
    ...validBasicTests,
    ...validRecursive,
    ...validClosures,
    ...validThisBinding,
    ...validAsyncGenerator,
    ...validIIFE,
    ...validDifferentScopes,
    ...validExportedFunctions,
    ...validSideEffects,
    ...validHoisting,
    ...validSpecialArguments,
    ...validFunctionProperties,
    ...validErrorHandling,
    ...validNameCollisions
  ],
  invalid: [
    ...invalidBasicTests,
    ...invalidFixerWhitespace,
    ...invalidMultiInstance
  ]
});

/**
 * @fileoverview Simplify boolean expressions and remove redundant logic
 * @author mojoatomic
 */
'use strict';

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

const rule = require('../../../lib/rules/prefer-simpler-logic'),
  RuleTester = require('eslint').RuleTester;


//------------------------------------------------------------------------------
// Tests
//------------------------------------------------------------------------------

const ruleTester = new RuleTester({
  languageOptions: { ecmaVersion: 2021, sourceType: 'module' }
});

//------------------------------------------------------------------------------
// Basic Functionality Tests
//------------------------------------------------------------------------------

const validBasicTests = [
    // Normal comparisons
    { code: 'if (x === y) {}' },
    { code: 'if (a !== b) {}' },
    { code: 'if (count > 0) {}' },

    // Different operands in logical expressions
    { code: 'if (x || y) {}' },
    { code: 'if (a && b) {}' },
    // Note: 'x && y || z' tested in edge cases

    // Complex but necessary expressions
    { code: 'if (x && y && z) {}' },
    { code: 'if (a || b || c) {}' },
    // Note: 'x && (y || z)' tested in edge cases

    // Boolean variables used correctly
    { code: 'if (isValid) {}' },
    { code: 'if (!isError) {}' },

    // Non-boolean literals
    { code: 'if (x === 0) {}' },
    { code: 'if (y === "test") {}' },
    { code: 'if (z === null) {}' },
];

//------------------------------------------------------------------------------
// Phase 1: Critical Edge Cases
//------------------------------------------------------------------------------

// Multiple Negation - Test negation chaining
const validMultipleNegation = [
  // Double negation for type coercion (common pattern)
  { code: 'if (!!x) {}' }, // Coerces to boolean
  { code: 'const bool = !!value;' }, // Common idiom

  // Single negation (normal)
  { code: 'if (!flag) {}' },
  { code: 'if (!x) {}' },

  // Triple negation (not currently detected by rule)
  { code: 'if (!!!x) {}' }, // Rule doesn't handle UnaryExpression chains yet
];

// Mixed Boolean and Non-Boolean - Rule simplifies boolean comparisons independently
const invalidMixedTypes = [
  // Mixed with non-boolean comparisons (boolean part still simplifies)
  {
    code: 'if (x === true || y > 5) {}',
    errors: [{ messageId: 'unnecessaryComparison' }],
    output: 'if (x || y > 5) {}'
  },
  {
    code: 'if (x === true && count > 0) {}',
    errors: [{ messageId: 'unnecessaryComparison' }],
    output: 'if (x && count > 0) {}'
  },
  {
    code: 'if (flag === false || str === "test") {}',
    errors: [{ messageId: 'unnecessaryComparison' }],
    output: 'if (!flag || str === "test") {}'
  },
];

// Type Coercion Differences - Different semantics, should stay valid
const validTypeDifferences = [
  // These have different semantics from x === true
  { code: 'if (x) {}' }, // Truthy check (0, "", null, undefined, false, NaN are falsy)
  // Note: !!x tested in validMultipleNegation
  // Note: x === true IS detected by the rule and simplified to x
];

// Type Coercion - Boolean() function usage
const validTypeCoercion = [
  // Boolean() function (similar to !!)
  { code: 'if (Boolean(x)) {}' },
  { code: 'const bool = Boolean(value);' },
  { code: 'function test() { return Boolean(flag); }' },
];

// Operator Precedence - && has higher precedence than ||
const validOperatorPrecedence = [
  // Precedence is clear (no redundancy)
  { code: 'if (x && y || z) {}' }, // (x && y) || z
  { code: 'if (a || b && c) {}' }, // a || (b && c)
  { code: 'if (x && (y || z)) {}' }, // Explicit grouping
];

// Short-Circuit Evaluation - Must preserve evaluation order
const validShortCircuit = [
  // Side effects matter
  { code: 'if (x || doSomething()) {}' },
  { code: 'if (getValue() && check()) {}' },
  { code: 'if (obj && obj.method()) {}' }, // Null check pattern
  { code: 'if (arr && arr.length > 0) {}' }, // Common pattern
];

// Parentheses and Grouping - Grouping without boolean literals
const validGrouping = [
  // Explicit grouping for readability (no boolean literals to simplify)
  // Note: 'x && (y || z)' tested in validOperatorPrecedence
  { code: 'if ((a || b) && c) {}' },
  { code: 'if ((x)) {}' }, // Extra parens around variable
  // Note: (x === true) still gets simplified even with parens
];

const invalidBasicTests = [
    // x === true  x
    {
      code: 'if (x === true) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (x) {}'
    },
    {
      code: 'if (isValid === true) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (isValid) {}'
    },
    {
      code: 'const result = value === true;',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'const result = value;'
    },

    // true === x  x
    {
      code: 'if (true === x) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (x) {}'
    },
    {
      code: 'if (true === isReady) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (isReady) {}'
    },

    // x === false  !x
    {
      code: 'if (x === false) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!x) {}'
    },
    {
      code: 'if (isError === false) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!isError) {}'
    },
    {
      code: 'const result = flag === false;',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'const result = !flag;'
    },

    // false === x  !x
    {
      code: 'if (false === x) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!x) {}'
    },
    {
      code: 'if (false === isActive) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!isActive) {}'
    },

    // x !== true  !x
    {
      code: 'if (x !== true) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!x) {}'
    },
    {
      code: 'if (isValid !== true) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!isValid) {}'
    },

    // true !== x  !x
    {
      code: 'if (true !== x) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!x) {}'
    },

    // x !== false  x
    {
      code: 'if (x !== false) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (x) {}'
    },
    {
      code: 'if (isError !== false) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (isError) {}'
    },

    // false !== x  x
    {
      code: 'if (false !== x) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (x) {}'
    },

    // x == true  x (loose equality)
    {
      code: 'if (x == true) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (x) {}'
    },
    {
      code: 'if (x == false) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!x) {}'
    },

    // x != true  !x (loose equality)
    {
      code: 'if (x != true) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (!x) {}'
    },
    {
      code: 'if (x != false) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'if (x) {}'
    },

    // x || x  x
    {
      code: 'if (x || x) {}',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'if (x) {}'
    },
    {
      code: 'if (isValid || isValid) {}',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'if (isValid) {}'
    },
    {
      code: 'const result = value || value;',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'const result = value;'
    },

    // x && x  x
    {
      code: 'if (x && x) {}',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'if (x) {}'
    },
    {
      code: 'if (isError && isError) {}',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'if (isError) {}'
    },
    {
      code: 'const result = flag && flag;',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'const result = flag;'
    },

    // x && y || y  x || y (absorbing element)
    {
      code: 'if (x && y || y) {}',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'if (x || y) {}'
    },
    {
      code: 'if (a && b || b) {}',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'if (a || b) {}'
    },
    {
      code: 'const result = isValid && isReady || isReady;',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'const result = isValid || isReady;'
    },

    // y && x || y  y || x (reverse order)
    {
      code: 'if (y && x || y) {}',
      errors: [{ messageId: 'redundantExpression' }],
      output: 'if (y || x) {}'
    },

    // Complex expressions
    {
      code: 'function test() { return condition === true; }',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'function test() { return condition; }'
    },
    {
      code: 'const fn = () => value === false;',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'const fn = () => !value;'
    },
    {
      code: 'while (running === true) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'while (running) {}'
    },
    {
      code: 'for (let i = 0; flag === true; i++) {}',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'for (let i = 0; flag; i++) {}'
    },

    // In ternary
    {
      code: 'const result = x === true ? a : b;',
      errors: [{ messageId: 'unnecessaryComparison' }],
      output: 'const result = x ? a : b;'
    },

    // Multiple issues in same statement
    {
      code: 'if (x === true || y === true) {}',
      errors: [
        { messageId: 'unnecessaryComparison' },
        { messageId: 'unnecessaryComparison' }
      ],
      output: 'if (x || y) {}'
    },
    {
      code: 'if (a === false && b === false) {}',
      errors: [
        { messageId: 'unnecessaryComparison' },
        { messageId: 'unnecessaryComparison' }
      ],
      output: 'if (!a && !b) {}'
    },
];

// Multi-instance: multiple boolean comparisons simplified in one block
const invalidMultiInstance = [
  {
    code: `
if (x === true) {}
if (isReady === true) {}
if (flag === false) {}
`,
    errors: [
      { messageId: 'unnecessaryComparison' },
      { messageId: 'unnecessaryComparison' },
      { messageId: 'unnecessaryComparison' }
    ],
    output: `
if (x) {}
if (isReady) {}
if (!flag) {}
`
  }
];

//------------------------------------------------------------------------------
// Run All Tests
//------------------------------------------------------------------------------

ruleTester.run('prefer-simpler-logic', rule, {
  valid: [
    ...validBasicTests,
    ...validMultipleNegation,
    ...validTypeDifferences,
    ...validTypeCoercion,
    ...validOperatorPrecedence,
    ...validShortCircuit,
    ...validGrouping
  ],
  invalid: [
    ...invalidBasicTests,
    ...invalidMixedTypes,
    ...invalidMultiInstance
  ]
});
