      // Skip if in comparison (returns boolean, not number)
      if (parent && ['BinaryExpression', 'LogicalExpression'].includes(parent.type)) {
        const comparisonOps = ['==', '===', '!=', '!==', '<', '>', '<=', '>='];
        if (comparisonOps.includes(parent.operator)) {
          return true;
