"use strict";

const assert = require('assert');
const fs = require('fs');
const os = require('os');
const path = require('path');
const { spawn } = require('child_process');

function writeFile(dir, rel, content) {
  const full = path.join(dir, rel);
  fs.mkdirSync(path.dirname(full), { recursive: true });
  fs.writeFileSync(full, content, 'utf8');
}

function runInteractiveLearn(tmpDir, inputs) {
  return new Promise((resolve, reject) => {
    const cliPath = path.resolve(__dirname, '..', '..', 'bin', 'cli.js');
    const env = { ...process.env, SKIP_AI_REQUIREMENTS: '1' };
    const proc = spawn('node', [cliPath, 'learn', '--interactive', '--sample=50', '--no-cache'], {
      cwd: tmpDir,
      env,
      stdio: ['pipe', 'pipe', 'pipe']
    });

    let stdout = '';
    let stderr = '';
    let inputIdx = 0;
    let responding = false;

    proc.stdout.on('data', (data) => {
      stdout += data.toString();
      // Auto-respond to prompts with debounce to avoid race conditions
      if (!responding && inputIdx < inputs.length) {
        responding = true;
        setTimeout(() => {
          if (inputIdx < inputs.length) {
            proc.stdin.write(inputs[inputIdx] + '\n');
            inputIdx++;
          }
          responding = false;
        }, 100);
      }
    });

    proc.stderr.on('data', (data) => {
      stderr += data.toString();
    });

    proc.on('close', (code) => {
      resolve({ code, stdout, stderr });
    });

    proc.on('error', reject);

    // Timeout fallback
    setTimeout(() => {
      proc.kill();
      reject(new Error('Process timeout'));
    }, 20000);
  });
}

