      code: `// @domains geometry\nconst deg = 720 / 2;`,
    },
    // Name-based: includes geometry term
    {
      code: `const circleAngle = 720 / 2;`,
    },
    // constantResolution mapping 360->geometry
    (() => {
      const t = tempConfigWithResolution({ '360': 'geometry' });
      return {
        code: `const z = 720 / 2;`,
        filename: t.filename
      };
    })(),
  ],
  invalid: []
});

