    errors: [
      { messageId: 'constantCondition' },
      { messageId: 'constantCondition' },
      { messageId: 'constantCondition' }
    ],
    output: `
A();

C();
`
  }
];

// Multi-instance: redundant ternaries in one block
const invalidMultiInstanceTernaries = [
  {
    code: `
const a = x ? true : false;
const b = y ? false : true;
const c = z ? value : value;
`,
    errors: [
      { messageId: 'redundantTernary' },
      { messageId: 'redundantTernary' },
      { messageId: 'redundantTernary' }
    ],
    output: `
const a = Boolean(x);
const b = !y;
const c = value;
`
  }
];

// Multi-instance: loop constants in one block
const invalidMultiInstanceLoops = [
  {
    code: `
while (false) { A(); }
for (; false; ) { B(); }
do { C(); } while (false);
`,
    errors: [
      { messageId: 'constantCondition' },
      { messageId: 'constantCondition' },
      { messageId: 'constantCondition' }
    ],
    output: `


C();
`
  }
];

//------------------------------------------------------------------------------
// Run All Tests
//------------------------------------------------------------------------------

ruleTester.run("no-redundant-conditionals", rule, {
  valid: [
    ...validBasicTests,
    ...validFalsyValues,
    ...validComplexTernaries,
    ...validNestedConditionals,
    ...validLogicalExpressions
  ],
  invalid: [
    ...invalidBasicTests,
    ...invalidFalsyValues,
    ...invalidTruthyValues,
    ...invalidComplexTernaries,
    ...invalidCoverageGaps,
    ...invalidConstantFolding,
    ...invalidSwitchConstants,
    ...invalidMultiInstance,
    ...invalidMultiInstanceTernaries,
    ...invalidMultiInstanceLoops
  ]
});

/**
 * @fileoverview Suggest inlining trivial single-use wrapper functions that add no value
 * @author mojoatomic
 */
"use strict";

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

const rule = require("../../../lib/rules/no-unnecessary-abstraction"),
  RuleTester = require("eslint").RuleTester;


//------------------------------------------------------------------------------
// Tests
//------------------------------------------------------------------------------

const ruleTester = new RuleTester({ languageOptions: { ecmaVersion: 2021, sourceType: 'module' } });

//------------------------------------------------------------------------------
// Basic Functionality Tests
//------------------------------------------------------------------------------

const validBasicTests = [
    // Function used multiple times - not a wrapper issue
    {
      code: `
function helper(x) { return x * 2; }
helper(5);
helper(10);
      `,
    },

    // Function with additional logic - not trivial
    {
      code: `
function calculateTax(amount) {
  console.log('Calculating tax');
  return getTax(amount);
}
function getTax(amount) { return amount * 0.08; }
calculateTax(100);
      `,
    },

    // Function with multiple statements - not trivial
    {
      code: `
function wrapper(x) {
  const result = compute(x);
  return result;
}
function compute(x) { return x * 2; }
wrapper(5);
      `,
    },

    // Function transforms arguments - not pass-through
    {
      code: `
function wrapper(x) {
  return compute(x * 2);
}
function compute(x) { return x + 1; }
wrapper(5);
      `,
    },

    // Different number of parameters
    {
      code: `
function wrapper(x) {
  return compute(x, 10);
}
function compute(a, b) { return a + b; }
wrapper(5);
      `,
    },

    // Arguments in different order
    {
      code: `
function wrapper(a, b) {
  return compute(b, a);
}
function compute(x, y) { return x - y; }
wrapper(5, 3);
      `,
    },

    // Arrow function used multiple times
    {
      code: `
const double = (x) => x * 2;
double(5);
double(10);
      `,
    },

    // No return statement
    {
      code: `
function sideEffect(x) {
  console.log(x);
}
sideEffect(5);
      `,
    },

    // Returns non-call expression
    {
      code: `
function getValue() {
  return 42;
}
getValue();
      `,
    },

    // Function never called
    {
      code: `
function wrapper(x) {
  return compute(x);
}
function compute(x) { return x * 2; }
      `,
    },

    // Method call wrapper used multiple times
    {
      code: `
function wrapper(obj) {
  return obj.method();
}
const o = { method() { return 1; } };
wrapper(o);
wrapper(o);
      `,
    },

    // Destructured parameters - not simple identifiers
    {
      code: `
function wrapper({ x }) {
  return compute({ x });
}
function compute(obj) { return obj.x * 2; }
wrapper({ x: 5 });
      `,
    },

    // Rest parameters
    {
      code: `
function wrapper(...args) {
  return compute(...args);
}
function compute(a, b) { return a + b; }
wrapper(1, 2);
      `,
    },

    // Default parameters
    {
      code: `
function wrapper(x = 0) {
  return compute(x);
}
function compute(x) { return x * 2; }
wrapper();
      `,
    },

    // Expression body arrow (no block statement)
    {
      code: `
const wrapper = (x) => compute(x);
function compute(x) { return x * 2; }
wrapper(5);
      `,
    },

    // Method call wrapper - not a simple identifier callee
    {
      code: `
function getLength(arr) {
  return arr.length();
}
const a = { length() { return 5; } };
getLength(a);
      `,
    },

    // Chained method call wrapper - not a simple identifier callee
    {
      code: `
function process(obj) {
  return obj.data.transform();
}
const o = { data: { transform() { return 1; } } };
process(o);
      `,
    },
];

//------------------------------------------------------------------------------
// Phase 1: Critical Edge Cases
//------------------------------------------------------------------------------

// Recursive Functions - Should NEVER be inlined
const validRecursive = [
  // Direct recursion
  {
    code: `
function factorial(n) {
  return n <= 1 ? 1 : n * factorial(n - 1);
}
factorial(5);
    `,
  },
  // Mutual recursion
  {
    code: `
function isEven(n) {
  return n === 0 ? true : isOdd(n - 1);
}
function isOdd(n) {
  return n === 0 ? false : isEven(n - 1);
}
isEven(4);
    `,
  },
];

// Closures - Functions capturing state from outer scope
const validClosures = [
  // Captures variable from outer scope
  {
    code: `
function makeCounter() {
  let count = 0;
  return function increment() {
    return ++count;
  };
}
const counter = makeCounter();
counter();
    `,
  },
  // Captures parameter from outer function
  {
    code: `
function multiplier(factor) {
  return function multiply(x) {
    return factor * x;
  };
}
